import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const Navbar = () => {
  const { user, logout, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <nav className="bg-x-black/80 backdrop-blur border-b border-x-border sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link
            to={user ? "/feed" : "/"}
            className="flex items-center space-x-2"
          >
            <div className="w-8 h-8 bg-x-blue rounded-full flex items-center justify-center">
              <span className="text-white font-bold text-sm">D</span>
            </div>
            <span className="text-2xl font-bold text-x-white">DevMate</span>
          </Link>

          {/* Navigation */}
          <div className="flex items-center space-x-4">
            {user ? (
              // Logged in navigation
              <>
                <Link
                  to="/feed"
                  className="text-x-white hover:text-x-blue transition-colors"
                >
                  Home
                </Link>
                <Link
                  to="/explore"
                  className="text-x-white hover:text-x-blue transition-colors"
                >
                  Explore
                </Link>
                <div className="flex items-center space-x-3">
                  <Link to={`/profile/${user.username}`}>
                    <div className="w-8 h-8 bg-x-blue rounded-full flex items-center justify-center">
                      <span className="text-white font-bold text-sm">
                        {user.displayName?.[0] || user.username?.[0]}
                      </span>
                    </div>
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="text-x-gray hover:text-x-white transition-colors"
                  >
                    Logout
                  </button>
                </div>
              </>
            ) : (
              // Public navigation
              <>
                <Link
                  to="/login"
                  className="text-x-white hover:text-x-blue transition-colors"
                >
                  Sign In
                </Link>
                <Link to="/register" className="x-button">
                  Get Started
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
