import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import LoadingSpinner from "../components/LoadingSpinner";

const EditProfile = () => {
  const { user, updateUser } = useAuth();
  const [formData, setFormData] = useState({
    displayName: user?.displayName || "",
    bio: user?.bio || "",
    skills: user?.skills || [],
    githubLink: user?.githubLink || "",
  });
  const [newSkill, setNewSkill] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
    if (error) setError("");
    if (success) setSuccess("");
  };

  const handleAddSkill = (e) => {
    e.preventDefault();
    if (newSkill.trim() && !formData.skills.includes(newSkill.trim())) {
      setFormData({
        ...formData,
        skills: [...formData.skills, newSkill.trim()],
      });
      setNewSkill("");
    }
  };

  const handleRemoveSkill = (skillToRemove) => {
    setFormData({
      ...formData,
      skills: formData.skills.filter((skill) => skill !== skillToRemove),
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);
      setError("");

      const response = await axios.put(`/api/users/${user.id}`, {
        displayName: formData.displayName.trim(),
        bio: formData.bio.trim(),
        skills: formData.skills,
        githubLink: formData.githubLink.trim(),
      });

      updateUser(response.data);
      setSuccess("Profile updated successfully!");

      // Redirect to profile after 2 seconds
      setTimeout(() => {
        navigate(`/profile/${user.username}`);
      }, 2000);
    } catch (error) {
      setError(error.response?.data?.message || "Failed to update profile");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto py-8 px-4">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Edit Profile</h1>
        <p className="text-gray-600">
          Update your profile information and skills.
        </p>
      </div>

      <form onSubmit={handleSubmit} className="card p-6">
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
            {error}
          </div>
        )}

        {success && (
          <div className="bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded-lg mb-6">
            {success}
          </div>
        )}

        <div className="space-y-6">
          {/* Display Name */}
          <div>
            <label
              htmlFor="displayName"
              className="block text-sm font-medium text-gray-700 mb-2"
            >
              Display Name
            </label>
            <input
              type="text"
              id="displayName"
              name="displayName"
              className="input-field"
              placeholder="Your display name"
              value={formData.displayName}
              onChange={handleChange}
              maxLength="50"
            />
            <p className="text-xs text-gray-500 mt-1">
              This is how your name will appear to other users
            </p>
          </div>

          {/* Bio */}
          <div>
            <label
              htmlFor="bio"
              className="block text-sm font-medium text-gray-700 mb-2"
            >
              Bio
            </label>
            <textarea
              id="bio"
              name="bio"
              rows="4"
              className="input-field resize-none"
              placeholder="Tell us about yourself, your interests, and what you're working on..."
              value={formData.bio}
              onChange={handleChange}
              maxLength="500"
            />
            <div className="flex justify-between items-center mt-1">
              <p className="text-xs text-gray-500">
                Share your background, interests, or current projects
              </p>
              <span className="text-xs text-gray-500">
                {formData.bio.length}/500
              </span>
            </div>
          </div>

          {/* Skills */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Skills
            </label>

            {/* Add new skill */}
            <div className="flex mb-4">
              <input
                type="text"
                value={newSkill}
                onChange={(e) => setNewSkill(e.target.value)}
                className="flex-1 input-field mr-2"
                placeholder="Add a skill (e.g., JavaScript, React, Python)"
                maxLength="30"
                onKeyPress={(e) => {
                  if (e.key === "Enter") {
                    handleAddSkill(e);
                  }
                }}
              />
              <button
                type="button"
                onClick={handleAddSkill}
                className="btn-primary"
                disabled={!newSkill.trim()}
              >
                Add
              </button>
            </div>

            {/* Skills list */}
            {formData.skills.length > 0 ? (
              <div className="flex flex-wrap gap-2">
                {formData.skills.map((skill, index) => (
                  <span
                    key={index}
                    className="bg-primary-100 text-primary-800 px-3 py-1 rounded-full text-sm font-medium flex items-center"
                  >
                    {skill}
                    <button
                      type="button"
                      onClick={() => handleRemoveSkill(skill)}
                      className="ml-2 text-primary-600 hover:text-primary-800"
                    >
                      ×
                    </button>
                  </span>
                ))}
              </div>
            ) : (
              <p className="text-sm text-gray-500 italic">
                No skills added yet
              </p>
            )}
            <p className="text-xs text-gray-500 mt-2">
              Add skills to help others find you and understand your expertise
            </p>
          </div>

          {/* GitHub Link */}
          <div>
            <label
              htmlFor="githubLink"
              className="block text-sm font-medium text-gray-700 mb-2"
            >
              GitHub Profile URL
            </label>
            <input
              type="url"
              id="githubLink"
              name="githubLink"
              className="input-field"
              placeholder="https://github.com/yourusername"
              value={formData.githubLink}
              onChange={handleChange}
            />
            <p className="text-xs text-gray-500 mt-1">
              Link to your GitHub profile to showcase your projects
            </p>
          </div>
        </div>

        <div className="flex justify-end space-x-4 mt-8 pt-6 border-t border-gray-200">
          <button
            type="button"
            onClick={() => navigate(`/profile/${user.username}`)}
            className="btn-secondary"
          >
            Cancel
          </button>
          <button
            type="submit"
            disabled={loading}
            className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
          >
            {loading ? (
              <>
                <LoadingSpinner size="small" className="mr-2" />
                Saving...
              </>
            ) : (
              "Save Changes"
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditProfile;
