import React from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const LogoutConfirm = () => {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleConfirm = () => {
    logout();
  };

  const handleCancel = () => {
    navigate(-1);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-x-black via-x-dark to-x-blue/40 px-4">
      <div className="bg-x-darker/90 rounded-3xl shadow-2xl p-8 sm:p-12 max-w-md w-full text-center border border-x-border/30 backdrop-blur-xl">
        <div className="flex flex-col items-center mb-6">
          <div className="bg-gradient-to-tr from-amber-400 via-yellow-300 to-orange-400 p-4 rounded-full shadow-lg mb-4 animate-pulse">
            <svg
              width="40"
              height="40"
              viewBox="0 0 24 24"
              fill="none"
              stroke="#222"
              strokeWidth="2.2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <path d="M17 16l4-4m0 0l-4-4m4 4H7" />
              <rect
                x="3"
                y="5"
                width="8"
                height="14"
                rx="2"
                fill="none"
                stroke="#222"
              />
            </svg>
          </div>
          <h2 className="text-2xl sm:text-3xl font-bold text-x-white mb-2">
            Confirm Logout
          </h2>
          <p className="text-x-gray text-base sm:text-lg">
            Are you sure you want to log out of your DevMate account?
          </p>
        </div>
        <div className="flex flex-col sm:flex-row gap-4 mt-8">
          <button
            onClick={handleConfirm}
            className="w-full sm:w-auto bg-amber-400 hover:bg-amber-500 text-x-black font-bold px-8 py-3 rounded-full shadow-md transition-all duration-200 text-lg focus:outline-none focus:ring-2 focus:ring-amber-400 focus:ring-offset-2"
          >
            Yes, Log me out
          </button>
          <button
            onClick={handleCancel}
            className="w-full sm:w-auto bg-x-dark hover:bg-x-darker text-x-white font-bold px-8 py-3 rounded-full shadow-md transition-all duration-200 text-lg border border-x-border/40"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default LogoutConfirm;
