import React from "react";
import { Link, useLocation } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const FloatingPostButton = () => {
  const { user } = useAuth();
  const location = useLocation();

  // Only show on specific pages when user is logged in
  const showOnPages = ["/feed", "/explore", "/profile", "/post"];
  const shouldShow = user && showOnPages.some(page => 
    location.pathname.startsWith(page) || location.pathname === "/"
  );

  // Don't show on create-post page itself
  if (!shouldShow || location.pathname === "/create-post") {
    return null;
  }

  return (
    <Link
      to="/create-post"
      className="lg:hidden fixed bottom-6 right-6 w-16 h-16 bg-gradient-to-r from-x-blue to-x-blue-hover hover:from-x-blue-hover hover:to-x-blue rounded-full flex items-center justify-center text-white shadow-2xl hover:shadow-x-blue/25 transition-all duration-300 transform hover:scale-110 z-50 group"
      title="Create Post"
    >
      <svg
        className="w-8 h-8 group-hover:rotate-12 transition-transform duration-300"
        fill="currentColor"
        viewBox="0 0 24 24"
      >
        <path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04c.39-.39.39-1.02 0-1.41l-2.34-2.34c-.39-.39-1.02-.39-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z" />
      </svg>
    </Link>
  );
};

export default FloatingPostButton;
