import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import LoadingSpinner from "../components/LoadingSpinner";

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
    // Clear error when user starts typing
    if (error) setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (dbConnected === false) {
      setError("Database connection required. Please start MongoDB service.");
      return;
    }

    setLoading(true);
    setError("");

    const result = await login(formData.email, formData.password);

    if (result.success) {
      navigate("/feed");
    } else {
      setError(result.error);
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-x-black via-x-dark to-x-black flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      {/* Background Effects */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-x-blue/5 rounded-full blur-3xl"></div>
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-x-green/5 rounded-full blur-3xl"></div>
      </div>

      <div className="relative max-w-md w-full">
        {/* Main Card */}
        <div className="bg-x-dark/80 backdrop-blur-xl border border-x-border/50 rounded-2xl shadow-2xl shadow-x-black/50 p-8 space-y-8">
          {/* Header */}
          <div className="text-center">
            <div className="flex justify-center mb-6">
              <div className="w-16 h-16 bg-gradient-to-r from-x-blue to-x-green rounded-2xl flex items-center justify-center shadow-lg shadow-x-blue/25">
                <span className="text-white font-bold text-2xl">D</span>
              </div>
            </div>
            <h1 className="text-3xl font-bold text-x-white mb-2">
              Welcome Back
            </h1>
            <p className="text-x-gray">
              Sign in to continue to{" "}
              <span className="text-x-blue font-semibold">DevMate</span>
            </p>
            <div className="mt-4 flex items-center justify-center space-x-2">
              <span className="text-x-gray text-sm">New here?</span>
              <Link
                to="/register"
                className="text-x-blue hover:text-x-blue-hover font-medium transition-colors"
              >
                Create account
              </Link>
            </div>
          </div>

          {/* Database Status Checker */}
          <DatabaseStatus
            onStatusChange={(status) => setDbConnected(status.connected)}
          />

          {/* Error Display */}
          {error && (
            <div className="bg-red-500/10 border border-red-500/20 text-red-400 px-4 py-3 rounded-xl backdrop-blur-sm">
              <div className="flex items-start space-x-3">
                <svg
                  className="w-5 h-5 mt-0.5 flex-shrink-0"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                >
                  <path
                    fillRule="evenodd"
                    d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z"
                    clipRule="evenodd"
                  />
                </svg>
                <div>
                  <p className="font-medium">{error}</p>
                  {(error.includes("Database") ||
                    error.includes("MongoDB")) && (
                    <p className="mt-1 text-sm text-red-300">
                      ðŸ’¡ <strong>Quick fix:</strong> Start MongoDB service and
                      try again
                    </p>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Login Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-4">
              <div>
                <label
                  htmlFor="email"
                  className="block text-sm font-medium text-x-white mb-2"
                >
                  Email Address
                </label>
                <input
                  id="email"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  className="w-full px-4 py-3 bg-x-black/50 border border-x-border/30 rounded-xl text-x-white placeholder-x-gray focus:outline-none focus:ring-2 focus:ring-x-blue/50 focus:border-x-blue transition-all duration-200"
                  placeholder="Enter your email"
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>

              <div>
                <label
                  htmlFor="password"
                  className="block text-sm font-medium text-x-white mb-2"
                >
                  Password
                </label>
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="w-full px-4 py-3 bg-x-black/50 border border-x-border/30 rounded-xl text-x-white placeholder-x-gray focus:outline-none focus:ring-2 focus:ring-x-blue/50 focus:border-x-blue transition-all duration-200"
                  placeholder="Enter your password"
                  value={formData.password}
                  onChange={handleChange}
                />
              </div>
            </div>

            <button
              type="submit"
              disabled={loading || dbConnected === false}
              className="w-full bg-gradient-to-r from-x-blue to-x-blue-hover text-white font-semibold py-3 px-6 rounded-xl transition-all duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none shadow-lg hover:shadow-x-blue/25 flex items-center justify-center"
              title={dbConnected === false ? "MongoDB connection required" : ""}
            >
              {loading ? (
                <>
                  <LoadingSpinner size="small" className="mr-2" />
                  Signing in...
                </>
              ) : dbConnected === false ? (
                <>
                  <svg
                    className="w-5 h-5 mr-2"
                    fill="currentColor"
                    viewBox="0 0 20 20"
                  >
                    <path
                      fillRule="evenodd"
                      d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z"
                      clipRule="evenodd"
                    />
                  </svg>
                  Database Required
                </>
              ) : (
                "Sign In"
              )}
            </button>

            {dbConnected === false && (
              <p className="text-sm text-x-gray text-center">
                Please start MongoDB to enable login
              </p>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
