import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const BottomNav = () => {
  const { user } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();

  // State for expanding the + menu
  const [plusMenuOpen, setPlusMenuOpen] = useState(false);

  // Close menu on route change
  useEffect(() => {
    setPlusMenuOpen(false);
  }, [location.pathname]);

  // Function to handle navigation click
  const handleNavClick = (path, e) => {
    e.preventDefault();
    // First navigate to the path
    navigate(path);
    // Then immediately scroll to top with instant behavior
    window.scrollTo({
      top: 0,
      left: 0,
      behavior: "instant",
    });
  };

  // Don't render if user is not logged in
  if (!user) return null;

  const isActive = (path) => {
    // Consider root path as feed
    if (
      path === "/feed" &&
      (location.pathname === "/" || location.pathname === "/feed")
    )
      return true;

    // For profile paths, check if we're on a profile page and it's the user's profile
    if (path.includes("/profile/") && location.pathname.includes("/profile/")) {
      return location.pathname === path;
    }

    // For other paths, exact match or starts with
    return (
      location.pathname === path ||
      (path !== "/feed" && location.pathname.startsWith(path))
    );
  };

  return (
    <div className="lg:hidden fixed bottom-0 left-0 right-0 z-50 border-t border-x-border/30 bg-x-black/90 backdrop-blur-md">
      <div className="flex items-center justify-around py-2 relative">
        {/* Feed Tab */}
        <Link
          to="/feed"
          className="flex flex-col items-center justify-center p-2 w-1/5"
          onClick={(e) => handleNavClick("/feed", e)}
        >
          {isActive("/feed") ? (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              fill="currentColor"
              className="w-6 h-6 text-x-blue"
            >
              <path d="M11.47 3.84a.75.75 0 011.06 0l8.69 8.69a.75.75 0 101.06-1.06l-8.689-8.69a2.25 2.25 0 00-3.182 0l-8.69 8.69a.75.75 0 001.061 1.06l8.69-8.69z" />
              <path d="M12 5.432l8.159 8.159c.03.03.06.058.091.086v6.198c0 1.035-.84 1.875-1.875 1.875H15a.75.75 0 01-.75-.75v-4.5a.75.75 0 00-.75-.75h-3a.75.75 0 00-.75.75v4.5a.75.75 0 01-.75.75H5.625a1.875 1.875 0 01-1.875-1.875v-6.198c.03-.028.061-.056.091-.086L12 5.432z" />
            </svg>
          ) : (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6 text-x-gray"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M2.25 12l8.954-8.955c.44-.439 1.152-.439 1.591 0L21.75 12M4.5 9.75v10.125c0 .621.504 1.125 1.125 1.125H9.75v-4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125V21h4.125c.621 0 1.125-.504 1.125-1.125V9.75M8.25 21h8.25"
              />
            </svg>
          )}
        </Link>
        {/* Explore Tab */}
        <Link
          to="/explore"
          className="flex flex-col items-center justify-center p-2 w-1/5"
          onClick={(e) => handleNavClick("/explore", e)}
        >
          {isActive("/explore") ? (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              fill="currentColor"
              className="w-6 h-6 text-x-blue"
            >
              <path
                fillRule="evenodd"
                d="M10.5 3.75a6.75 6.75 0 100 13.5 6.75 6.75 0 000-13.5zM2.25 10.5a8.25 8.25 0 1114.59 5.28l4.69 4.69a.75.75 0 11-1.06 1.06l-4.69-4.69A8.25 8.25 0 012.25 10.5z"
                clipRule="evenodd"
              />
            </svg>
          ) : (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6 text-x-gray"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M21 21l-5.197-5.197m0 0A7.5 7.5 0 105.196 5.196a7.5 7.5 0 0010.607 10.607z"
              />
            </svg>
          )}
        </Link>
        {/* Animated + Tab */}
        <div className="relative flex flex-col items-center justify-center w-1/5">
          <button
            className={`flex items-center justify-center rounded-full bg-x-blue shadow-lg transition-transform duration-300 focus:outline-none border-4 border-x-black ${
              plusMenuOpen ? "scale-110 rotate-45" : "scale-100"
            } animate-bounce`}
            style={{ width: 48, height: 48, marginTop: -24, zIndex: 20 }}
            onClick={() => setPlusMenuOpen((v) => !v)}
            aria-label="Open quick actions"
          >
            <svg
              width="28"
              height="28"
              viewBox="0 0 24 24"
              fill="none"
              stroke="white"
              strokeWidth="2.5"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <line x1="12" y1="5" x2="12" y2="19" />
              <line x1="5" y1="12" x2="19" y2="12" />
            </svg>
          </button>
          {/* Quick Actions Menu */}
          {plusMenuOpen && (
            <div className="absolute bottom-14 left-1/2 -translate-x-1/2 flex flex-col items-center gap-2 bg-x-dark/90 rounded-xl shadow-xl p-2 animate-fade-in z-30">
              <button
                className="w-40 py-2 px-4 rounded-lg bg-x-blue/80 text-white font-semibold text-sm hover:bg-x-green/80 transition-colors"
                onClick={() => {
                  setPlusMenuOpen(false);
                  navigate("/create-post");
                }}
              >
                ✍️ Create Post
              </button>
              <button
                className="w-40 py-2 px-4 rounded-lg bg-x-blue/80 text-white font-semibold text-sm hover:bg-x-green/80 transition-colors"
                onClick={() => {
                  setPlusMenuOpen(false);
                  alert("Upload Image (demo)");
                }}
              >
                🖼️ Upload Image
              </button>
              <button
                className="w-40 py-2 px-4 rounded-lg bg-x-blue/80 text-white font-semibold text-sm hover:bg-x-green/80 transition-colors"
                onClick={() => {
                  setPlusMenuOpen(false);
                  alert("Ask Question (demo)");
                }}
              >
                ❓ Ask Question
              </button>
            </div>
          )}
        </div>
        {/* Messages Tab */}
        <Link
          to="/messages"
          className="flex flex-col items-center justify-center p-2 w-1/5"
          onClick={(e) => handleNavClick("/messages", e)}
        >
          {isActive("/messages") ? (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              fill="currentColor"
              className="w-6 h-6 text-x-blue"
            >
              <path d="M4.913 2.658c2.075-.27 4.19-.408 6.337-.408 2.147 0 4.262.139 6.337.408 1.922.25 3.291 1.861 3.405 3.727a4.403 4.403 0 00-1.032-.211 50.89 50.89 0 00-8.42 0c-2.358.196-4.04 2.19-4.04 4.434v4.286a4.47 4.47 0 002.433 3.984L7.28 21.53A.75.75 0 016 21v-4.03a48.527 48.527 0 01-1.087-.128C2.905 16.58 1.5 14.833 1.5 12.862V6.638c0-1.97 1.405-3.718 3.413-3.979z" />
              <path d="M15.75 7.5c-1.376 0-2.739.057-4.086.169C10.124 7.797 9 9.103 9 10.609v4.285c0 1.507 1.128 2.814 2.67 2.94 1.243.102 2.5.157 3.768.165l2.782 2.781a.75.75 0 001.28-.53v-2.39l.33-.026c1.542-.125 2.67-1.433 2.67-2.94v-4.286c0-1.505-1.125-2.811-2.664-2.94A49.392 49.392 0 0015.75 7.5z" />
            </svg>
          ) : (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6 text-x-gray"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M8.625 9.75a.375.375 0 11-.75 0 .375.375 0 01.75 0zm0 0H8.25m4.125 0a.375.375 0 11-.75 0 .375.375 0 01.75 0zm0 0H12m4.125 0a.375.375 0 11-.75 0 .375.375 0 01.75 0zm0 0h-.375m-13.5 3.01c0 1.6 1.123 2.994 2.707 3.227 1.087.16 2.185.283 3.293.369V21l4.184-4.183a1.14 1.14 0 01.778-.332 48.294 48.294 0 005.83-.498c1.585-.233 2.708-1.626 2.708-3.228V6.741c0-1.602-1.123-2.995-2.707-3.228A48.394 48.394 0 0012 3c-2.392 0-4.744.175-7.043.513C3.373 3.746 2.25 5.14 2.25 6.741v6.018z"
              />
            </svg>
          )}
        </Link>
        {/* Profile Tab */}
        <Link
          to={`/profile/${user?.username}`}
          className="flex flex-col items-center justify-center p-2 w-1/5"
          onClick={(e) => handleNavClick(`/profile/${user?.username}`, e)}
        >
          {isActive(`/profile/${user?.username}`) ? (
            <div className="w-6 h-6 rounded-full border-2 border-x-blue flex items-center justify-center overflow-hidden bg-gradient-to-r from-x-blue to-x-green">
              <span className="text-white text-xs font-bold">
                {user.displayName?.[0] || user.username?.[0]}
              </span>
            </div>
          ) : (
            <div className="w-6 h-6 rounded-full border border-x-gray flex items-center justify-center overflow-hidden">
              <span className="text-x-gray text-xs font-bold">
                {user.displayName?.[0] || user.username?.[0]}
              </span>
            </div>
          )}
        </Link>
      </div>
    </div>
  );
};

export default BottomNav;
