import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  // Dark mode state (for demo, you can connect to your theme logic)
  const [dark, setDark] = useState(true);

  const toggleDark = () => {
    setDark((d) => !d);
    // Add your theme switching logic here if needed
    document.documentElement.classList.toggle("dark", !dark);
  };

  return (
    <nav className="fixed top-0 left-0 w-full bg-gradient-to-r from-x-black via-x-dark to-x-black/95 backdrop-blur-xl border-b border-x-border/50 z-[60] shadow-lg shadow-x-black/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo Only */}
          <Link to="/" className="flex items-center group">
            <div className="w-9 h-9 bg-x-blue rounded-full flex items-center justify-center group-hover:bg-x-blue-hover transition-colors">
              <span className="text-white font-bold text-lg">D</span>
            </div>
          </Link>

          {/* Dark Mode Toggle */}
          <button
            onClick={toggleDark}
            className="ml-4 p-2 rounded-full bg-x-dark hover:bg-x-blue transition-colors text-x-gray hover:text-x-white focus:outline-none"
            aria-label="Toggle dark mode"
          >
            {dark ? (
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                strokeWidth={2}
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M12 3v1m0 16v1m8.66-13.66l-.71.71M4.05 19.95l-.71.71M21 12h-1M4 12H3m16.95 7.05l-.71-.71M4.05 4.05l-.71-.71M16 12a4 4 0 11-8 0 4 4 0 018 0z"
                />
              </svg>
            ) : (
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                strokeWidth={2}
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M21 12.79A9 9 0 1111.21 3a7 7 0 109.79 9.79z"
                />
              </svg>
            )}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
