import React from "react";
import { Link } from "react-router-dom";

const features = [
  {
    icon: "‚ö°",
    title: "Instant Code Sharing",
    desc: "Share code snippets instantly with the community, just like tweeting your thoughts.",
    color: "bg-blue-500"
  },
  {
    icon: "üåê",
    title: "Global Developer Network",
    desc: "Connect with developers worldwide, follow, and collaborate on projects.",
    color: "bg-green-500"
  },
  {
    icon: "üí¨",
    title: "Real-time Discussions",
    desc: "Comment, like, and discuss code in real-time, Twitter-style threads.",
    color: "bg-purple-500"
  },
  {
    icon: "üìà",
    title: "Portfolio Growth",
    desc: "Build your public profile and showcase your best work to the world.",
    color: "bg-pink-500"
  },
  {
    icon: "ü§ù",
    title: "Collaboration Tools",
    desc: "Work together on code, get feedback, and grow as a team.",
    color: "bg-yellow-500"
  },
  {
    icon: "üîí",
    title: "Secure & Private",
    desc: "Your code and data are protected with industry-standard security.",
    color: "bg-gray-700"
  },
  {
    icon: "üé®",
    title: "Customizable Profiles",
    desc: "Personalize your profile with avatars, bios, and social links.",
    color: "bg-indigo-500"
  },
  {
    icon: "üì±",
    title: "Mobile Friendly",
    desc: "Enjoy a seamless experience on any device, just like Twitter.",
    color: "bg-blue-700"
  },
  {
    icon: "üîî",
    title: "Notifications",
    desc: "Stay updated with real-time notifications for likes, comments, and follows.",
    color: "bg-red-500"
  },
  {
    icon: "üßë‚Äçüíª",
    title: "Verified Developer Badges",
    desc: "Earn badges and recognition for your contributions.",
    color: "bg-green-700"
  }
];

const Features = () => (
  <div className="min-h-screen bg-x-black text-white flex flex-col w-full">
    <header className="w-full border-b border-x-border/40 bg-gradient-to-r from-x-black via-x-dark to-x-black/95 px-8 py-6 flex items-center justify-between">
      <Link to="/" className="flex items-center space-x-2 group">
        <div className="w-9 h-9 bg-x-blue rounded-full flex items-center justify-center group-hover:bg-x-blue-hover transition-colors">
          <span className="text-white font-bold text-lg">D</span>
        </div>
        <span className="text-2xl font-bold text-x-white group-hover:text-x-blue transition-colors">DevMate</span>
      </Link>
      <nav className="hidden md:flex items-center space-x-6">
        <Link to="/features" className="text-x-blue font-bold border-b-2 border-x-blue">Features</Link>
        <Link to="/explore" className="text-x-gray hover:text-x-blue transition-colors">Explore</Link>
        <Link to="/register" className="bg-x-blue hover:bg-x-blue-hover text-white font-bold px-5 py-2 rounded-full transition-all duration-200 hover:scale-105 shadow-lg">Register</Link>
      </nav>
    </header>
    <main className="flex-1 w-full max-w-5xl mx-auto px-4 py-12">
      <h1 className="text-4xl md:text-5xl font-bold text-center mb-8 text-x-blue">Features</h1>
      <p className="text-center text-x-gray mb-12 text-lg max-w-2xl mx-auto">Discover all the powerful features DevMate offers to help you connect, code, and grow‚Äîstyled with a modern Twitter-inspired look.</p>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {features.map((f, i) => (
          <div key={i} className="rounded-2xl shadow-xl bg-x-dark/80 border border-x-border/30 p-8 flex flex-col items-center text-center hover:scale-105 transition-transform group">
            <div className={`w-16 h-16 flex items-center justify-center rounded-full text-3xl mb-4 shadow-lg ${f.color}`}>{f.icon}</div>
            <h2 className="text-2xl font-bold mb-2 text-x-white group-hover:text-x-blue transition-colors">{f.title}</h2>
            <p className="text-x-gray mb-2">{f.desc}</p>
          </div>
        ))}
      </div>
      <div className="text-center mt-16">
        <Link to="/register" className="inline-block bg-x-blue hover:bg-x-blue-hover text-white font-bold text-xl px-10 py-4 rounded-full transition-all duration-300 shadow-2xl hover:scale-105">Get Started Free</Link>
      </div>
    </main>
  </div>
);

export default Features;
