import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RightSidebar = () => {
  const [trends, setTrends] = useState([]);
  const [suggestedUsers, setSuggestedUsers] = useState([]);

  useEffect(() => {
    // Mock trending topics
    setTrends([
      { tag: 'JavaScript', posts: 1234 },
      { tag: 'React', posts: 987 },
      { tag: 'Node.js', posts: 654 },
      { tag: 'MongoDB', posts: 432 },
      { tag: 'MERN', posts: 321 }
    ]);

    // Fetch suggested users
    fetchSuggestedUsers();
  }, []);

  const fetchSuggestedUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuggestedUsers(response.data.slice(0, 3));
    } catch (error) {
      console.error('Error fetching suggested users:', error);
    }
  };

  return (
    <div className="x-rightbar space-y-6">
      {/* Trending */}
      <div className="x-card">
        <h2 className="text-xl font-bold mb-4">What's happening</h2>
        <div className="space-y-3">
          {trends.map((trend, index) => (
            <div
              key={index}
              className="hover:bg-x-darker p-2 rounded cursor-pointer transition-colors"
            >
              <p className="text-x-gray text-sm">Trending in Programming</p>
              <p className="font-bold">#{trend.tag}</p>
              <p className="text-x-gray text-sm">{trend.posts.toLocaleString()} posts</p>
            </div>
          ))}
        </div>
      </div>

      {/* Who to follow */}
      <div className="x-card">
        <h2 className="text-xl font-bold mb-4">Who to follow</h2>
        <div className="space-y-3">
          {suggestedUsers.map((user) => (
            <div key={user._id} className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-x-blue rounded-full flex items-center justify-center">
                  <span className="text-white font-bold">
                    {user.displayName?.[0] || user.username?.[0]}
                  </span>
                </div>
                <div>
                  <p className="font-bold">{user.displayName || user.username}</p>
                  <p className="text-x-gray text-sm">@{user.username}</p>
                </div>
              </div>
              <button className="x-button-outline text-sm px-4 py-1">
                Follow
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RightSidebar;
