import React, { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import ShimmerEffect from "../components/ShimmerEffect";
import LoadingSpinner from "../components/LoadingSpinner";

const Explore = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState("");
  const [selectedSkill, setSelectedSkill] = useState("");
  const [isMobile, setIsMobile] = useState(false);

  // Check if device is mobile on mount and window resize
  useEffect(() => {
    const checkIfMobile = () => {
      setIsMobile(window.innerWidth < 768); // 768px is the md breakpoint in Tailwind
    };

    // Check on mount
    checkIfMobile();

    // Add resize listener
    window.addEventListener("resize", checkIfMobile);

    // Cleanup
    return () => {
      window.removeEventListener("resize", checkIfMobile);
    };
  }, []);

  // Function to determine if we should show results
  const shouldShowResults = () => {
    // Always show on desktop
    if (!isMobile) return true;
    // On mobile, only show if search/filter is active
    return isMobile && (debouncedSearchTerm.trim() !== "" || selectedSkill !== "");
  };
  
  // Create a fixed height container effect
  const [contentHeight, setContentHeight] = useState('auto');
  
  useEffect(() => {
    // Set fixed height for search results area to prevent layout shifts
    if (isMobile) {
      const resultsContainer = document.querySelector('.results-container');
      if (resultsContainer && resultsContainer.offsetHeight > 0) {
        setContentHeight(`${resultsContainer.offsetHeight}px`);
      }
    }
  }, [isMobile]);

  // Debounce search term to reduce API calls and UI flicker
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 300); // 300ms delay

    return () => clearTimeout(timer);
  }, [searchTerm]);

  useEffect(() => {
    fetchUsers();
  }, [debouncedSearchTerm, selectedSkill]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (debouncedSearchTerm) params.append("search", debouncedSearchTerm);
      if (selectedSkill) params.append("skill", selectedSkill);

      const response = await axios.get(`/api/users?${params.toString()}`);
      setUsers(response.data);
    } catch (error) {
      setError("Failed to fetch users");
      console.error("Fetch users error:", error);
    } finally {
      setLoading(false);
    }
  };

  // Get all unique skills from users for filter dropdown
  const allSkills = [...new Set(users.flatMap((user) => user.skills))].sort();

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSkillChange = (e) => {
    setSelectedSkill(e.target.value);
  };

  const clearFilters = () => {
    setSearchTerm("");
    setSelectedSkill("");
  };

  return (
    <div className="w-full max-w-7xl mx-auto py-2 sm:py-4 lg:py-8 px-3 sm:px-4 lg:px-6 pb-20 lg:pb-8">
      {/* Hero Section */}
      <div className="mb-4 sm:mb-6 lg:mb-8">
        <h1 className="text-lg sm:text-xl md:text-2xl lg:text-3xl font-bold bg-gradient-to-r from-x-blue via-purple-500 to-x-green bg-[length:200%_auto] bg-clip-text text-transparent md:animate-color-cycle mb-2 lg:mb-3">
          Explore Developers
        </h1>
        <p className="text-x-gray text-sm sm:text-base lg:text-base max-w-2xl">
          Discover and connect with talented developers in the DevMate
          community. Find your next collaborator, mentor, or coding buddy.
        </p>
      </div>

      {/* Search and Filter Controls */}
      <div className="card p-3 sm:p-4 lg:p-8 mb-2 sm:mb-3 lg:mb-4 bg-gradient-to-r from-x-dark/50 to-x-dark/30 md:backdrop-blur-sm backdrop-blur-none border border-x-border/50">
        <div className="flex flex-col gap-3 sm:gap-4 lg:gap-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-3 sm:gap-4 lg:gap-6">
            <div>
              <label
                htmlFor="search"
                className="block text-sm font-medium text-x-white mb-2 sm:mb-3"
              >
                üîç Search by name or username
              </label>
              <div className="relative">
                <input
                  type="text"
                  id="search"
                  className="w-full bg-x-black/60 border border-x-border text-x-white placeholder-x-gray rounded-xl px-4 py-3 focus:ring-2 focus:ring-x-blue focus:border-x-blue"
                  placeholder="Search developers..."
                  value={searchTerm}
                  onChange={handleSearchChange}
                />
                <svg
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-x-gray"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                  />
                </svg>
              </div>
            </div>

            <div>
              <label
                htmlFor="skill"
                className="block text-sm font-medium text-x-white mb-3"
              >
                üè∑Ô∏è Filter by skill
              </label>
              <select
                id="skill"
                className="w-full bg-x-black/60 border border-x-border text-x-white rounded-xl px-4 py-3 focus:ring-2 focus:ring-x-blue focus:border-x-blue"
                value={selectedSkill}
                onChange={handleSkillChange}
              >
                <option value="">All skills</option>
                {allSkills.map((skill) => (
                  <option key={skill} value={skill}>
                    {skill}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="flex justify-center h-10">
            {(searchTerm || selectedSkill) && (
              <button
                onClick={clearFilters}
                className="btn-outline px-6 py-2 whitespace-nowrap"
              >
                ‚ú® Clear Filters
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Results */}
      {loading ? (
        <ShimmerEffect type="explore" />
      ) : error ? (
        <div className="bg-red-500/10 border border-red-500/20 text-red-400 px-6 py-4 rounded-xl backdrop-blur-sm">
          <div className="flex items-center">
            <svg
              className="w-5 h-5 mr-2"
              fill="currentColor"
              viewBox="0 0 20 20"
            >
              <path
                fillRule="evenodd"
                d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z"
                clipRule="evenodd"
              />
            </svg>
            {error}
          </div>
        </div>
      ) : users.length === 0 || (!shouldShowResults()) ? (
        <div className="text-center py-8 min-h-[200px] flex flex-col items-center justify-center">
          <div className="bg-x-blue/10 w-24 h-24 rounded-full flex items-center justify-center mx-auto mb-6">
            <svg
              className="w-12 h-12 text-x-blue"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z"
              />
            </svg>
          </div>
          <h3 className="text-xl font-semibold text-x-white mb-2">
            {users.length === 0 ? "No developers found" : "Search for developers"}
          </h3>
          <p className="text-x-gray max-w-md mx-auto">
            {users.length === 0 && (debouncedSearchTerm || selectedSkill)
              ? "Try adjusting your search criteria or clear the filters to see all developers."
              : users.length === 0
              ? "No developers have joined the community yet. Be the first to explore and connect!"
              : isMobile
              ? "Enter a search term or select a skill to find developers on DevMate."
              : ""}
          </p>
        </div>
      ) : (
        <>
          <div className="mb-3 min-h-[200px]">
            <div className="flex items-center justify-between">
              <p className="text-x-gray">
                <span className="text-x-white font-semibold">
                  {users.length}
                </span>{" "}
                developer{users.length !== 1 ? "s" : ""} found
                {debouncedSearchTerm && (
                  <span className="text-x-blue"> matching "{debouncedSearchTerm}"</span>
                )}
                {selectedSkill && (
                  <span className="text-x-green">
                    {" "}
                    with skill "{selectedSkill}"
                  </span>
                )}
              </p>
              <div className="hidden md:block text-sm text-x-gray">
                üí° Click on profiles to connect
              </div>
            </div>
          </div>

          <div className="space-y-4 lg:space-y-6">
            {users.map((user) => (
              <div
                key={user._id}
                className="w-full card p-4 lg:p-6 md:hover:shadow-xl md:hover:shadow-x-blue/20 md:hover:border-x-blue/30 md:transition-all md:duration-300 group bg-gradient-to-br from-x-dark/80 to-x-dark/40 md:backdrop-blur-sm backdrop-blur-none border border-x-border/30"
              >
                <div className="w-full">
                  {/* Mobile Layout - Avatar and View Profile Button Horizontal */}
                  <div className="flex items-center justify-between sm:hidden mb-4">
                    <div className="flex items-center gap-3">
                      <div className="bg-gradient-to-r from-x-blue to-purple-500 text-white w-12 h-12 rounded-2xl flex items-center justify-center text-lg font-bold shadow-lg md:group-hover:shadow-x-blue/30 md:transition-all md:duration-300">
                        {user.displayName?.charAt(0).toUpperCase() ||
                          user.username.charAt(0).toUpperCase()}
                      </div>
                      <div className="flex-1 min-w-0">
                        <h3 className="font-bold text-x-white text-base md:group-hover:text-x-blue md:transition-colors md:duration-200 mb-1">
                          {user.displayName || user.username}
                        </h3>
                        <p className="text-xs text-x-gray">@{user.username}</p>
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      {user.githubLink && (
                        <a
                          href={user.githubLink}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-x-gray md:hover:text-x-white md:transition-colors md:duration-200 p-2 rounded-lg md:hover:bg-x-dark/60"
                          title="GitHub Profile"
                        >
                          <svg
                            className="w-5 h-5"
                            fill="currentColor"
                            viewBox="0 0 20 20"
                          >
                            <path
                              fillRule="evenodd"
                              d="M10 0C4.477 0 0 4.484 0 10.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0110 4.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.203 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.942.359.31.678.921.678 1.856 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0020 10.017C20 4.484 15.522 0 10 0z"
                              clipRule="evenodd"
                            />
                          </svg>
                        </a>
                      )}
                      <Link
                        to={`/profile/${user.username}`}
                        className="btn-primary text-xs px-3 py-2 md:hover:scale-105 transform md:transition-all md:duration-200 whitespace-nowrap"
                      >
                        View Profile
                      </Link>
                    </div>
                  </div>

                  {/* Desktop Layout - Side by Side with Action Buttons */}
                  <div className="hidden sm:flex items-start justify-between gap-6 mb-4">
                    <div className="flex items-start gap-6 flex-1">
                      <div className="flex-shrink-0">
                        <div className="bg-gradient-to-r from-x-blue to-purple-500 text-white w-16 h-16 rounded-2xl flex items-center justify-center text-xl font-bold shadow-lg md:group-hover:shadow-x-blue/30 md:transition-all md:duration-300">
                          {user.displayName?.charAt(0).toUpperCase() ||
                            user.username.charAt(0).toUpperCase()}
                        </div>
                      </div>
                      <div className="flex-1 min-w-0">
                        <h3 className="font-bold text-x-white text-xl md:group-hover:text-x-blue md:transition-colors md:duration-200 mb-1">
                          {user.displayName || user.username}
                        </h3>
                        <p className="text-base text-x-gray mb-3">
                          @{user.username}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center gap-3 flex-shrink-0">
                      {user.githubLink && (
                        <a
                          href={user.githubLink}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-x-gray md:hover:text-x-white md:transition-colors md:duration-200 md:hover:scale-110 transform p-2 rounded-lg md:hover:bg-x-dark/60"
                          title="GitHub Profile"
                        >
                          <svg
                            className="w-6 h-6"
                            fill="currentColor"
                            viewBox="0 0 20 20"
                          >
                            <path
                              fillRule="evenodd"
                              d="M10 0C4.477 0 0 4.484 0 10.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0110 4.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.203 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.942.359.31.678.921.678 1.856 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0020 10.017C20 4.484 15.522 0 10 0z"
                              clipRule="evenodd"
                            />
                          </svg>
                        </a>
                      )}
                      <Link
                        to={`/profile/${user.username}`}
                        className="btn-primary text-base px-6 py-3 md:hover:scale-105 transform md:transition-all md:duration-200 whitespace-nowrap"
                      >
                        View Profile
                      </Link>
                    </div>
                  </div>

                  {/* Bio - Full Width */}
                  {user.bio && (
                    <div className="w-full mb-4">
                      <p className="text-x-gray text-sm lg:text-base leading-relaxed text-left line-clamp-3">
                        "{user.bio}"
                      </p>
                    </div>
                  )}

                  {/* Skills and Meta Info - Full Width */}
                  <div className="w-full flex flex-col sm:flex-row sm:items-center justify-between gap-4 pt-4 border-t border-x-border/30">
                    {/* Skills */}
                    {user.skills && user.skills.length > 0 && (
                      <div className="flex-1">
                        <div className="flex flex-wrap gap-2 justify-start">
                          {user.skills.slice(0, 8).map((skill, index) => (
                            <span
                              key={index}
                              className="bg-x-blue/20 text-x-blue border border-x-blue/30 px-3 py-1 rounded-full text-sm font-medium md:hover:bg-x-blue/30 md:transition-colors md:duration-200"
                            >
                              {skill}
                            </span>
                          ))}
                          {user.skills.length > 8 && (
                            <span className="text-sm text-x-gray px-3 py-1 bg-x-dark/60 rounded-full border border-x-border">
                              +{user.skills.length - 8} more
                            </span>
                          )}
                        </div>
                      </div>
                    )}

                    {/* Join Date */}
                    <div className="flex items-center justify-start text-sm text-x-gray flex-shrink-0">
                      <svg
                        className="w-4 h-4 mr-2"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"
                        />
                      </svg>
                      Joined{" "}
                      {new Date(user.createdAt).toLocaleDateString("en-US", {
                        year: "numeric",
                        month: "short",
                      })}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

