import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";

const SettingPage = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [passwords, setPasswords] = useState({
    current: "",
    new: "",
    confirm: "",
  });
  const [notifications, setNotifications] = useState({
    followers: true,
    comments: true,
    messages: true,
  });
  const [isPrivate, setIsPrivate] = useState(false);
  const [showDelete, setShowDelete] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [deleteError, setDeleteError] = useState("");

  const handlePasswordChange = (e) => {
    setPasswords({ ...passwords, [e.target.name]: e.target.value });
  };

  const handleNotificationsChange = (e) => {
    setNotifications({ ...notifications, [e.target.name]: e.target.checked });
  };

  const handlePrivacyToggle = () => setIsPrivate((v) => !v);

  const handleDeleteAccount = async () => {
    setDeleteLoading(true);
    setDeleteError("");
    try {
      await axios.delete(`/api/users/${user.id}`);
      logout();
      navigate("/");
    } catch (err) {
      setDeleteError(
        err.response?.data?.message ||
          "Failed to delete account. Please try again."
      );
    } finally {
      setDeleteLoading(false);
      setShowDelete(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto py-12 px-4">
      <h2 className="text-2xl font-bold mb-8 text-center">Settings</h2>

      {/* Change Password */}
      <div className="mb-8 p-6 bg-white/5 rounded-xl border border-gray-700">
        <h3 className="font-semibold mb-4">Change Password</h3>
        <input
          type="password"
          name="current"
          placeholder="Current password"
          value={passwords.current}
          onChange={handlePasswordChange}
          className="w-full mb-3 p-3 rounded bg-gray-900 border border-gray-700 text-white"
        />
        <input
          type="password"
          name="new"
          placeholder="New password"
          value={passwords.new}
          onChange={handlePasswordChange}
          className="w-full mb-3 p-3 rounded bg-gray-900 border border-gray-700 text-white"
        />
        <input
          type="password"
          name="confirm"
          placeholder="Confirm new password"
          value={passwords.confirm}
          onChange={handlePasswordChange}
          className="w-full mb-4 p-3 rounded bg-gray-900 border border-gray-700 text-white"
        />
        <button className="w-full py-2 rounded bg-blue-600 text-white font-medium hover:bg-blue-700 transition">
          Update Password
        </button>
      </div>

      {/* Email Notifications */}
      <div className="mb-8 p-6 bg-white/5 rounded-xl border border-gray-700">
        <h3 className="font-semibold mb-4">Email Notifications</h3>
        <label className="flex items-center mb-2">
          <input
            type="checkbox"
            name="followers"
            checked={notifications.followers}
            onChange={handleNotificationsChange}
            className="mr-2 accent-blue-600"
          />
          New followers
        </label>
        <label className="flex items-center mb-2">
          <input
            type="checkbox"
            name="comments"
            checked={notifications.comments}
            onChange={handleNotificationsChange}
            className="mr-2 accent-blue-600"
          />
          Comments on my posts
        </label>
        <label className="flex items-center">
          <input
            type="checkbox"
            name="messages"
            checked={notifications.messages}
            onChange={handleNotificationsChange}
            className="mr-2 accent-blue-600"
          />
          New messages
        </label>
      </div>

      {/* Account Privacy */}
      <div className="mb-8 p-6 bg-white/5 rounded-xl border border-gray-700 flex items-center justify-between">
        <span className="font-semibold">Account Privacy</span>
        <button
          onClick={handlePrivacyToggle}
          className={`w-12 h-6 flex items-center rounded-full p-1 transition-colors duration-200 ${
            isPrivate ? "bg-blue-600" : "bg-gray-700"
          }`}
        >
          <span
            className={`h-4 w-4 bg-white rounded-full shadow transform transition-transform duration-200 ${
              isPrivate ? "translate-x-6" : ""
            }`}
          />
        </button>
        <span className="ml-3 text-sm text-gray-400">
          {isPrivate ? "Private" : "Public"}
        </span>
      </div>

      {/* Delete Account */}
      <div className="p-6 bg-white/5 rounded-xl border border-gray-700 text-center">
        <button
          onClick={() => setShowDelete(true)}
          className="text-red-500 font-semibold hover:underline"
        >
          Delete Account
        </button>
        {showDelete && (
          <div className="mt-4 bg-gray-900 border border-gray-700 rounded-xl p-4">
            <p className="mb-4 text-red-400">
              Are you sure you want to delete your account? This action cannot
              be undone.
            </p>
            {deleteError && (
              <div className="mb-2 text-red-400 text-sm">{deleteError}</div>
            )}
            <button
              onClick={handleDeleteAccount}
              className="bg-red-600 text-white px-4 py-2 rounded mr-2 disabled:opacity-60"
              disabled={deleteLoading}
            >
              {deleteLoading ? "Deleting..." : "Yes, Delete"}
            </button>
            <button
              onClick={() => setShowDelete(false)}
              className="bg-gray-700 text-white px-4 py-2 rounded"
              disabled={deleteLoading}
            >
              Cancel
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default SettingPage;
