import React from "react";

const LoadingSpinner = ({ size = "large", className = "", fullWidth = false }) => {
  const sizeClasses = {
    small: "w-4 h-4",
    medium: "w-8 h-8",
    large: "w-12 h-12",
  };

  const content = (
    <div className={`flex justify-center items-center ${className}`}>
      <div
        className={`${sizeClasses[size]} border-4 border-gray-200 border-t-x-blue rounded-full animate-spin`}
      ></div>
    </div>
  );

  if (fullWidth) {
    return (
      <div className="max-w-2xl mx-auto py-8 lg:py-16 px-2 lg:px-4 min-h-[400px]">
        {content}
      </div>
    );
  }

  return content;
};

export default LoadingSpinner;
