import React, { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

const PostCard = ({ post, onUpdate, onDelete }) => {
  const { user } = useAuth();
  const [showCommentForm, setShowCommentForm] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [visibleCommentsCount, setVisibleCommentsCount] = useState(3);
  const [newComment, setNewComment] = useState("");
  const [loading, setLoading] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);

  const isLiked = post.likes.some((like) => like.user === user?.id);
  const isAuthor = post.author._id === user?.id;

  const handleLike = async () => {
    try {
      const response = await axios.put(`/api/posts/${post._id}/like`);
      onUpdate(response.data);
    } catch (error) {
      console.error("Like error:", error);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
      setLoading(true);
      const response = await axios.post(`/api/posts/${post._id}/comment`, {
        text: newComment.trim(),
      });
      onUpdate(response.data);
      setNewComment("");
      setShowCommentForm(false);
    } catch (error) {
      console.error("Comment error:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    setShowDeleteModal(false);
    setIsDeleting(true);
    try {
      await axios.delete(`/api/posts/${post._id}`);
      onDelete(post._id);
    } catch (error) {
      console.error("Delete error:", error);
    } finally {
      setIsDeleting(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = (now - date) / (1000 * 60 * 60);

    if (diffInHours < 1) {
      return "Just now";
    } else if (diffInHours < 24) {
      return `${Math.floor(diffInHours)}h ago`;
    } else {
      return date.toLocaleDateString();
    }
  };

  return (
    <div className="card p-6">
      {/* Post Header */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center">
          <div className="bg-x-blue text-white w-10 h-10 rounded-full flex items-center justify-center mr-3">
            {post.author.displayName?.charAt(0).toUpperCase() ||
              post.author.username.charAt(0).toUpperCase()}
          </div>
          <div>
            <Link
              to={`/profile/${post.author.username}`}
              className="font-semibold text-x-white hover:text-x-blue transition-colors"
            >
              {post.author.displayName || post.author.username}
            </Link>
            <p className="text-sm text-x-gray">
              @{post.author.username} • {formatDate(post.createdAt)}
            </p>
          </div>
        </div>

        {isAuthor && (
          <button
            onClick={() => setShowDeleteModal(true)}
            className={`text-x-gray hover:text-red-400 p-1 transition-colors ${
              isDeleting ? "opacity-50 cursor-not-allowed" : ""
            }`}
            title="Delete post"
            disabled={isDeleting}
          >
            {isDeleting ? (
              <svg
                className="w-5 h-5 animate-spin"
                fill="none"
                viewBox="0 0 24 24"
              >
                <circle
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                  className="opacity-25"
                ></circle>
                <path
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  className="opacity-75"
                ></path>
              </svg>
            ) : (
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                <path
                  fillRule="evenodd"
                  d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z"
                  clipRule="evenodd"
                />
              </svg>
            )}
          </button>
        )}
      </div>

      {/* Post Content */}
      <div className="mb-4 space-y-4">
        {/* Text Content with Visual Identity */}
        <div className="bg-x-dark/15 border border-x-border/20 rounded-lg p-4">
          <div className="flex items-center mb-3">
            <svg
              className="w-3.5 h-3.5 text-x-blue mr-2"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
              />
            </svg>
            <span className="text-xs font-semibold text-x-blue uppercase tracking-wide">
              Post
            </span>
          </div>
          <p className="text-x-white text-base leading-relaxed whitespace-pre-wrap">
            {post.content}
          </p>
        </div>

        {/* Code Section with Enhanced Visual Identity */}
        {post.codeSnippet && (
          <div className="code-snippet">
            <div className="flex items-center justify-between bg-x-dark/60 px-4 py-2 border-b border-x-border/30">
              <div className="flex items-center space-x-2">
                <div className="flex space-x-1">
                  <div className="w-2.5 h-2.5 rounded-full bg-red-500/60"></div>
                  <div className="w-2.5 h-2.5 rounded-full bg-yellow-500/60"></div>
                  <div className="w-2.5 h-2.5 rounded-full bg-green-500/60"></div>
                </div>
                <span className="text-xs text-x-gray font-mono">💻 Code</span>
              </div>
              <span className="text-xs text-x-blue font-mono uppercase tracking-wide">
                Code
              </span>
            </div>
            <div className="p-4">
              <pre className="whitespace-pre-wrap">{post.codeSnippet}</pre>
            </div>
          </div>
        )}
      </div>

      {/* Post Actions */}
      <div className="flex items-center space-x-6 py-2 border-t border-x-border">
        <button
          onClick={handleLike}
          className={`flex items-center space-x-2 text-sm ${
            isLiked ? "text-red-500" : "text-x-gray hover:text-red-500"
          } transition-colors`}
        >
          <svg
            className="w-5 h-5"
            fill={isLiked ? "currentColor" : "none"}
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
            />
          </svg>
          <span className="text-x-white font-medium">{post.likes.length}</span>
        </button>

        <button
          onClick={() => setShowCommentForm(!showCommentForm)}
          className="flex items-center space-x-2 text-sm text-x-gray hover:text-x-blue transition-colors"
        >
          <svg
            className="w-5 h-5"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"
            />
          </svg>
          <span className="text-x-white font-medium">
            {post.comments.length}
          </span>
        </button>

        <Link
          to={`/post/${post._id}`}
          className="text-sm text-x-gray hover:text-x-blue transition-colors"
        >
          View Details
        </Link>
      </div>

      {/* Comments Section */}
      {post.comments.length > 0 && (
        <div className="mt-4 pt-4 border-t border-x-border">
          <div className="space-y-3">
            {post.comments.slice(-2).map((comment) => (
              <div key={comment._id} className="flex space-x-3">
                <div className="bg-x-blue text-white w-8 h-8 rounded-full flex items-center justify-center text-sm">
                  {comment.user.displayName?.charAt(0).toUpperCase() ||
                    comment.user.username.charAt(0).toUpperCase()}
                </div>
                <div className="flex-1">
                  <div className="bg-x-dark/40 rounded-lg px-3 py-2">
                    <Link
                      to={`/profile/${comment.user.username}`}
                      className="font-medium text-sm text-x-white hover:text-x-blue"
                    >
                      {comment.user.displayName || comment.user.username}
                    </Link>
                    <p className="text-sm text-x-gray mt-1">{comment.text}</p>
                  </div>
                  <p className="text-xs text-x-gray/70 mt-1">
                    {formatDate(comment.createdAt)}
                  </p>
                </div>
              </div>
            ))}
          </div>

          {post.comments.length > 2 && (
            <Link
              to={`/post/${post._id}`}
              className="text-sm text-x-blue hover:text-x-blue-hover mt-2 inline-block"
            >
              View all {post.comments.length} comments
            </Link>
          )}
        </div>
      )}

      {/* Comment Form */}
      {showCommentForm && (
        <div className="mt-4 pt-4 border-t border-x-border">
          <form onSubmit={handleComment} className="flex space-x-3">
            <div className="bg-x-blue text-white w-8 h-8 rounded-full flex items-center justify-center text-sm">
              {user?.displayName?.charAt(0).toUpperCase() ||
                user?.username?.charAt(0).toUpperCase()}
            </div>
            <div className="flex-1">
              <textarea
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Write a comment..."
                className="w-full p-2 bg-x-black/50 border border-x-border text-x-white placeholder-x-gray rounded-lg resize-none focus:ring-2 focus:ring-x-blue focus:border-x-blue"
                rows="2"
                maxLength="500"
              />
              <div className="flex justify-between items-center mt-2">
                <span className="text-xs text-x-gray">
                  {newComment.length}/500
                </span>
                <div className="space-x-2">
                  <button
                    type="button"
                    onClick={() => {
                      setShowCommentForm(false);
                      setNewComment("");
                    }}
                    className="btn-secondary text-sm"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={!newComment.trim() || loading}
                    className="btn-primary text-sm disabled:opacity-50"
                  >
                    {loading ? "Posting..." : "Comment"}
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      )}

      {/* Custom Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-gradient-to-br from-x-dark to-x-darker border border-red-500/30 rounded-2xl shadow-2xl max-w-md w-full transform transition-all duration-300 scale-100">
            {/* Modal Header */}
            <div className="p-6 border-b border-red-500/20">
              <div className="flex items-center">
                <div className="bg-red-500/20 rounded-full p-3 mr-4">
                  <svg
                    className="w-6 h-6 text-red-400"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.732-.833-2.464 0L4.35 16.5c-.77.833.192 2.5 1.732 2.5z"
                    />
                  </svg>
                </div>
                <div>
                  <h3 className="text-lg font-bold text-x-white">
                    Delete Post
                  </h3>
                  <p className="text-sm text-x-gray">
                    This action cannot be undone
                  </p>
                </div>
              </div>
            </div>

            {/* Modal Content */}
            <div className="p-6">
              <p className="text-x-white mb-2">
                Are you sure you want to delete this post?
              </p>
              <p className="text-x-gray text-sm">
                This will permanently remove your post and all its comments.
                This action cannot be reversed.
              </p>

              {/* Post Preview */}
              <div className="mt-4 bg-x-black/40 border border-x-border/30 rounded-lg p-3">
                <p className="text-x-gray text-sm line-clamp-2">
                  "{post.content.substring(0, 100)}
                  {post.content.length > 100 ? "..." : ""}"
                </p>
              </div>
            </div>

            {/* Modal Actions */}
            <div className="p-6 pt-0 flex space-x-3">
              <button
                onClick={() => setShowDeleteModal(false)}
                className="flex-1 px-4 py-3 bg-x-dark/60 hover:bg-x-dark/80 border border-x-border text-x-white rounded-xl transition-all duration-200 font-medium"
              >
                Cancel
              </button>
              <button
                onClick={handleDelete}
                className="flex-1 px-4 py-3 bg-gradient-to-r from-red-600 to-red-500 hover:from-red-700 hover:to-red-600 text-white rounded-xl transition-all duration-200 font-medium shadow-lg hover:shadow-red-500/25"
              >
                🗑️ Delete Post
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PostCard;
