import React, { useState, useEffect } from "react";

const TrendingNews = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTrendingNews = async () => {
      try {
        setLoading(true);
        const response = await fetch("https://dev.to/api/articles?per_page=8&top=7");
        if (!response.ok) throw new Error("Failed to fetch articles");
        const data = await response.json();
        setArticles(data);
      } catch (err) {
        setError("Unable to load trending news");
        console.error("Error fetching articles:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchTrendingNews();
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", {
      month: "short",
      day: "numeric"
    });
  };

  const truncateTitle = (title, maxLength = 60) => {
    return title.length > maxLength ? title.substring(0, maxLength) + "..." : title;
  };

  if (loading) {
    return (
      <aside className="hidden xl:block xl:w-80 xl:ml-8">
        <div className="bg-gradient-to-br from-gray-900 to-gray-800 rounded-2xl p-6 border border-gray-700 shadow-2xl">
          <div className="flex items-center gap-3 mb-6">
            <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
              <svg className="w-4 h-4 text-white animate-spin" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
              </svg>
            </div>
            <h2 className="text-xl font-bold text-white">Trending Dev News</h2>
          </div>
          <div className="space-y-4">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="animate-pulse">
                <div className="h-4 bg-gray-600 rounded mb-2"></div>
                <div className="h-3 bg-gray-700 rounded w-2/3"></div>
              </div>
            ))}
          </div>
        </div>
      </aside>
    );
  }

  if (error) {
    return (
      <aside className="hidden xl:block xl:w-80 xl:ml-8">
        <div className="bg-gradient-to-br from-red-900 to-red-800 rounded-2xl p-6 border border-red-700 shadow-2xl">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-8 h-8 bg-red-600 rounded-full flex items-center justify-center">
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h2 className="text-xl font-bold text-white">Dev News</h2>
          </div>
          <p className="text-red-200">{error}</p>
        </div>
      </aside>
    );
  }

  return (
    <aside className="hidden xl:block xl:w-80 xl:ml-8">
      <div className="bg-gradient-to-br from-gray-900 to-gray-800 rounded-2xl p-6 border border-gray-700 shadow-2xl backdrop-blur-sm">
        {/* Header */}
        <div className="flex items-center gap-3 mb-6">
          <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
            <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 20H5a2 2 0 01-2-2V6a2 2 0 012-2h10a2 2 0 012 2v1m2 13a2 2 0 01-2-2V7m2 13a2 2 0 002-2V9.5a2.5 2.5 0 00-2.5-2.5H15" />
            </svg>
          </div>
          <h2 className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
            Trending Dev News
          </h2>
        </div>

        {/* Articles */}
        <div className="space-y-4 max-h-96 overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800">
          {articles.map((article, index) => (
            <article
              key={article.id}
              className="group cursor-pointer transform transition-all duration-300 hover:scale-105"
            >
              <a
                href={article.url}
                target="_blank"
                rel="noopener noreferrer"
                className="block p-4 rounded-xl bg-gradient-to-r from-gray-800 to-gray-700 border border-gray-600 hover:border-blue-500 transition-all duration-300 hover:shadow-lg hover:shadow-blue-500/25"
              >
                {/* Article ranking */}
                <div className="flex items-start gap-3">
                  <div className="flex-shrink-0 w-6 h-6 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-xs font-bold text-white">
                    {index + 1}
                  </div>
                  
                  <div className="flex-1 min-w-0">
                    {/* Title */}
                    <h3 className="text-sm font-semibold text-white mb-2 leading-snug group-hover:text-blue-400 transition-colors">
                      {truncateTitle(article.title)}
                    </h3>
                    
                    {/* Meta info */}
                    <div className="flex items-center justify-between text-xs text-gray-400">
                      <div className="flex items-center gap-2">
                        <span className="truncate max-w-20">{article.user?.name || article.user?.username}</span>
                        <span>•</span>
                        <span>{formatDate(article.published_at)}</span>
                      </div>
                      
                      {/* Engagement stats */}
                      <div className="flex items-center gap-2">
                        {article.public_reactions_count > 0 && (
                          <div className="flex items-center gap-1">
                            <svg className="w-3 h-3 text-red-500" fill="currentColor" viewBox="0 0 24 24">
                              <path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/>
                            </svg>
                            <span>{article.public_reactions_count}</span>
                          </div>
                        )}
                        
                        {article.comments_count > 0 && (
                          <div className="flex items-center gap-1">
                            <svg className="w-3 h-3 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                            </svg>
                            <span>{article.comments_count}</span>
                          </div>
                        )}
                      </div>
                    </div>
                    
                    {/* Tags */}
                    {article.tag_list && article.tag_list.length > 0 && (
                      <div className="flex flex-wrap gap-1 mt-2">
                        {article.tag_list.slice(0, 2).map((tag, tagIndex) => (
                          <span
                            key={tagIndex}
                            className="px-2 py-1 text-xs bg-blue-600/20 text-blue-400 rounded-full border border-blue-600/30"
                          >
                            #{tag}
                          </span>
                        ))}
                        {article.tag_list.length > 2 && (
                          <span className="px-2 py-1 text-xs bg-gray-600/20 text-gray-400 rounded-full border border-gray-600/30">
                            +{article.tag_list.length - 2}
                          </span>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </a>
            </article>
          ))}
        </div>

        {/* Footer */}
        <div className="mt-6 pt-4 border-t border-gray-700">
          <a
            href="https://dev.to"
            target="_blank"
            rel="noopener noreferrer"
            className="flex items-center justify-center gap-2 text-xs text-gray-400 hover:text-blue-400 transition-colors"
          >
            <span>Powered by</span>
            <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
              <path d="M7.42 10.05c-.18-.16-.46-.23-.84-.23H6l.02 2.44.04 2.45.92-.02c.68-.02 1.16-.04 1.4-.15.17-.08.28-.17.33-.32.04-.15.05-.2.05-.78-.01-.65-.09-.87-.23-1.05-.17-.23-.45-.33-.91-.33h-.19l.02-.01zM21 5.5c0 2.75-2.25 5-5 5h-.5v1.5c0 .83-.67 1.5-1.5 1.5s-1.5-.67-1.5-1.5v-5c0-.83.67-1.5 1.5-1.5s1.5.67 1.5 1.5V10h.5c1.93 0 3.5-1.57 3.5-3.5S17.43 3 15.5 3c-1.06 0-2.02.47-2.67 1.22-.42-.47-1.02-.72-1.63-.72-.83 0-1.5.67-1.5 1.5v5c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5V6.5c0-.28.22-.5.5-.5s.5.22.5.5v3c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5V5.5c0-.28.22-.5.5-.5s.5.22.5.5z"/>
            </svg>
            <span className="font-semibold">DEV.to</span>
          </a>
        </div>
      </div>
    </aside>
  );
};

export default TrendingNews;
