import React, { useState, useRef, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const Navbar = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const menuRef = useRef(null);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMobileMenuOpen(false);
      }
    }
    function handleScroll() {
      setMobileMenuOpen(false);
      setScrolled(window.scrollY > 4);
    }
    if (mobileMenuOpen) {
      document.addEventListener("mousedown", handleClickOutside);
      window.addEventListener("scroll", handleScroll);
    } else {
      document.removeEventListener("mousedown", handleClickOutside);
      window.removeEventListener("scroll", handleScroll);
    }
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      window.removeEventListener("scroll", handleScroll);
    };
  }, [mobileMenuOpen]);

  // Always listen for scroll to toggle blur
  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 4);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <nav className={`fixed top-0 left-0 w-full z-[60] border-b border-x-border/50 shadow-lg shadow-x-black/20 bg-gradient-to-r from-x-black via-x-dark to-x-black/95 transition-all duration-300 ${scrolled ? "backdrop-blur-xl" : "backdrop-blur-none"}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link
            to={user ? "/feed" : "/"}
            className="flex items-center space-x-2 group"
          >
            <div className="w-8 h-8 bg-x-blue rounded-full flex items-center justify-center group-hover:bg-x-blue-hover transition-colors">
              <span className="text-white font-bold text-sm">D</span>
            </div>
            <span className="text-2xl font-bold text-x-white group-hover:text-x-blue transition-colors">
              DevMate
            </span>
          </Link>

          {/* Navigation */}
          <div className="flex items-center space-x-6">
            {user ? (
              // Logged in navigation
              <>
                {/* Desktop Navigation */}
                <div className="hidden md:flex items-center space-x-6">
                  <Link
                    to="/feed"
                    className="relative text-x-white hover:text-x-blue transition-all duration-200 font-medium group"
                  >
                    <span>Home</span>
                    <div className="absolute bottom-0 left-0 w-0 h-0.5 bg-x-blue group-hover:w-full transition-all duration-200"></div>
                  </Link>
                  <Link
                    to="/explore"
                    className="relative text-x-white hover:text-x-blue transition-all duration-200 font-medium group"
                  >
                    <span>Explore</span>
                    <div className="absolute bottom-0 left-0 w-0 h-0.5 bg-x-blue group-hover:w-full transition-all duration-200"></div>
                  </Link>
                  <Link
                    to="/create-post"
                    className="relative text-x-white hover:text-x-blue transition-all duration-200 font-medium group"
                  >
                    <span>Create</span>
                    <div className="absolute bottom-0 left-0 w-0 h-0.5 bg-x-blue group-hover:w-full transition-all duration-200"></div>
                  </Link>
                </div>

                {/* User Menu - Desktop */}
                <div className="hidden md:flex items-center space-x-4">
                  <Link to={`/profile/${user.username}`} className="group">
                    <div className="w-9 h-9 bg-gradient-to-r from-x-blue to-x-green rounded-full flex items-center justify-center group-hover:scale-110 transition-transform shadow-lg">
                      <span className="text-white font-bold text-sm">
                        {user.displayName?.[0] || user.username?.[0]}
                      </span>
                    </div>
                  </Link>
                  <button
                    onClick={() => navigate("/logout-confirm")}
                    className="px-4 py-2 text-x-gray hover:text-x-white hover:bg-x-dark rounded-lg transition-all duration-200 font-medium"
                  >
                    Logout
                  </button>
                </div>

                {/* Mobile Menu Button - Simplified */}
                <div className="md:hidden flex items-center space-x-3 relative">
                  <button
                    onClick={() => setMobileMenuOpen((v) => !v)}
                    className="focus:outline-none"
                    aria-label="Open user menu"
                  >
                    {mobileMenuOpen ? (
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="w-8 h-8 text-x-gray bg-x-dark rounded-full p-1 border border-x-border/40 shadow-lg"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        strokeWidth={2}
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          d="M6 18L18 6M6 6l12 12"
                        />
                      </svg>
                    ) : (
                      <div className="w-8 h-8 bg-gradient-to-r from-x-blue to-x-green rounded-full flex items-center justify-center transition-transform shadow-lg">
                        <span className="text-white font-bold text-xs">
                          {user.displayName?.[0] || user.username?.[0]}
                        </span>
                      </div>
                    )}
                  </button>
                  {mobileMenuOpen && (
                    <div
                      ref={menuRef}
                      className="absolute right-0 top-12 z-50 w-44 bg-x-dark border border-x-border/40 rounded-xl shadow-xl py-2 flex flex-col animate-fade-in"
                    >
                      <Link
                        to={`/profile/${user.username}`}
                        className="px-4 py-3 text-x-white hover:bg-x-darker rounded-t-xl transition-colors text-left"
                        onClick={() => setMobileMenuOpen(false)}
                      >
                        View Profile
                      </Link>
                      <Link
                        to="/settings"
                        className="px-4 py-3 text-x-white hover:bg-x-darker transition-colors text-left"
                        onClick={() => setMobileMenuOpen(false)}
                      >
                        Settings
                      </Link>
                      <button
                        onClick={() => {
                          setMobileMenuOpen(false);
                          navigate("/logout-confirm");
                        }}
                        className="px-4 py-3 text-red-400 hover:bg-red-900/40 rounded-b-xl transition-colors text-left text-base font-medium"
                      >
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              </>
            ) : (
              // Public navigation
              <>
                <div className="hidden md:flex items-center space-x-6">
                  <a
                    href="#features"
                    className="text-x-gray hover:text-x-white transition-colors font-medium"
                  >
                    Features
                  </a>
                  <a
                    href="#community"
                    className="text-x-gray hover:text-x-white transition-colors font-medium"
                  >
                    Community
                  </a>
                </div>
                <div className="flex items-center space-x-3">
                  <Link
                    to="/login"
                    className="px-4 py-2 text-x-white hover:text-x-blue transition-all duration-200 font-medium hover:bg-x-dark rounded-lg"
                  >
                    Sign In
                  </Link>
                  <Link
                    to="/register"
                    className="bg-gradient-to-r from-x-blue to-x-blue-hover text-white font-medium px-5 py-2.5 rounded-full transition-all duration-200 transform hover:scale-105 shadow-lg hover:shadow-x-blue/25"
                  >
                    Get Started
                  </Link>
                </div>

                {/* Mobile Profile Only */}
                <div className="md:hidden">
                  <Link
                    to={`/profile/${user?.username || ""}`}
                    className="group"
                  >
                    <div className="w-8 h-8 bg-gradient-to-r from-x-blue to-x-green rounded-full flex items-center justify-center group-hover:scale-110 transition-transform shadow-lg">
                      <span className="text-white font-bold text-xs">
                        {user?.displayName?.[0] || user?.username?.[0] || "U"}
                      </span>
                    </div>
                  </Link>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
