import React, { useEffect, useState } from "react";

const RightSidebar = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch trending dev news from Dev.to API
    fetch("https://dev.to/api/articles?per_page=6&top=7")
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch news");
        return res.json();
      })
      .then((data) => {
        setArticles(data);
        setLoading(false);
      })
      .catch((err) => {
        setError("Could not load news");
        setLoading(false);
      });
  }, []);

  return (
    <aside className="x-rightbar bg-x-dark space-y-6 hidden xl:flex xl:flex-col h-full xl:pl-4 xl:w-80 rounded-2xl mt-4">
      <div className="x-card">
        <h2 className="text-xl font-bold mb-4 text-blue-400">
          Trending Dev News
        </h2>
        {loading && <div className="text-x-gray">Loading...</div>}
        {error && <div className="text-red-400">{error}</div>}
        <ul className="space-y-3">
          {articles.map((article) => (
            <li key={article.id}>
              <a
                href={article.url}
                target="_blank"
                rel="noopener noreferrer"
                className="block font-semibold text-x-white hover:text-blue-400 transition-colors truncate"
                title={article.title}
              >
                {article.title}
              </a>
              <div className="text-xs text-x-gray mt-1 flex items-center gap-2">
                <span>
                  {article.user?.name || article.user?.username}
                </span>
                <span>â€¢</span>
                <span>
                  {new Date(article.published_at).toLocaleDateString()}
                </span>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </aside>
  );
};

export default RightSidebar;
