import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { AuthProvider } from "./contexts/AuthContext";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Feed from "./pages/Feed";
import CreatePost from "./pages/CreatePost";
import PostDetail from "./pages/PostDetail";
import Profile from "./pages/Profile";
import EditProfile from "./pages/EditProfile";
import Explore from "./pages/Explore";
import ProtectedRoute from "./components/ProtectedRoute";
import PublicRoute from "./components/PublicRoute";
import Sidebar from "./components/Sidebar";
import RightSidebar from "./components/RightSidebar";
import Footer from "./components/Footer";
import FloatingPostButton from "./components/FloatingPostButton";
import BottomNav from "./components/BottomNav";
import { useAuth } from "./contexts/AuthContext";
import LogoutConfirm from "./pages/LogoutConfirm";

function AppContent() {
  const { user } = useAuth();

  return (
    <div className="min-h-screen bg-x-black text-x-white flex flex-col">
      <Navbar />

      {user ? (
        // Logged in layout - Responsive 3-column
        <>
          <div className="flex-1 flex flex-col">
            <div className="x-container-mobile">
              <Sidebar />
              <main className="x-main-mobile">
                <Routes>
                  <Route
                    path="/"
                    element={
                      <ProtectedRoute>
                        <Feed />
                      </ProtectedRoute>
                    }
                  />
                  <Route
                    path="/feed"
                    element={
                      <ProtectedRoute>
                        <Feed />
                      </ProtectedRoute>
                    }
                  />
                  <Route
                    path="/create-post"
                    element={
                      <ProtectedRoute>
                        <CreatePost />
                      </ProtectedRoute>
                    }
                  />
                  <Route
                    path="/post/:id"
                    element={
                      <ProtectedRoute>
                        <PostDetail />
                      </ProtectedRoute>
                    }
                  />
                  <Route
                    path="/profile/:username"
                    element={
                      <ProtectedRoute>
                        <Profile />
                      </ProtectedRoute>
                    }
                  />
                  <Route
                    path="/edit-profile"
                    element={
                      <ProtectedRoute>
                        <EditProfile />
                      </ProtectedRoute>
                    }
                  />
                  <Route
                    path="/explore"
                    element={
                      <ProtectedRoute>
                        <Explore />
                      </ProtectedRoute>
                    }
                  />
                  <Route
                    path="/logout-confirm"
                    element={
                      <ProtectedRoute>
                        <LogoutConfirm />
                      </ProtectedRoute>
                    }
                  />
                  <Route path="*" element={<Navigate to="/feed" />} />
                </Routes>
              </main>
              <RightSidebar />
            </div>
          </div>
          <Footer />
        </>
      ) : (
        // Not logged in layout - full width
        <div className="w-full">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route
              path="/login"
              element={
                <div className="flex justify-center items-center min-h-screen px-4">
                  <div className="w-full max-w-md">
                    <PublicRoute>
                      <Login />
                    </PublicRoute>
                  </div>
                </div>
              }
            />
            <Route
              path="/register"
              element={
                <div className="flex justify-center items-center min-h-screen px-4">
                  <div className="w-full max-w-md">
                    <PublicRoute>
                      <Register />
                    </PublicRoute>
                  </div>
                </div>
              }
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
      )}

      {/* Global Floating Post Button for Mobile */}
      {/* <FloatingPostButton /> */}

      {/* Bottom Navigation for Mobile */}
      <BottomNav />
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}

export default App;
