import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import LoadingSpinner from "../components/LoadingSpinner";

const Explore = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedSkill, setSelectedSkill] = useState("");

  useEffect(() => {
    fetchUsers();
  }, [searchTerm, selectedSkill]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (searchTerm) params.append("search", searchTerm);
      if (selectedSkill) params.append("skill", selectedSkill);

      const response = await axios.get(`/api/users?${params.toString()}`);
      setUsers(response.data);
    } catch (error) {
      setError("Failed to fetch users");
      console.error("Fetch users error:", error);
    } finally {
      setLoading(false);
    }
  };

  // Get all unique skills from users for filter dropdown
  const allSkills = [...new Set(users.flatMap((user) => user.skills))].sort();

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSkillChange = (e) => {
    setSelectedSkill(e.target.value);
  };

  const clearFilters = () => {
    setSearchTerm("");
    setSelectedSkill("");
  };

  return (
    <div className="max-w-6xl mx-auto py-8 px-4">
      {/* Hero Section */}
      <div className="text-center mb-12">
        <h1 className="text-4xl md:text-5xl font-bold bg-gradient-to-r from-x-blue via-purple-500 to-x-green bg-[length:200%_auto] bg-clip-text text-transparent animate-color-cycle mb-4">
          Explore Developers
        </h1>
        <p className="text-x-gray text-lg max-w-2xl mx-auto">
          Discover and connect with talented developers in the DevMate community. 
          Find your next collaborator, mentor, or coding buddy.
        </p>
      </div>

      {/* Search and Filter Controls */}
      <div className="card p-8 mb-8 bg-gradient-to-r from-x-dark/50 to-x-dark/30 backdrop-blur-sm border border-x-border/50">
        <div className="flex flex-col lg:flex-row gap-6">
          <div className="flex-1">
            <label htmlFor="search" className="block text-sm font-medium text-x-white mb-3">
              üîç Search by name or username
            </label>
            <div className="relative">
              <input
                type="text"
                id="search"
                className="w-full bg-x-black/60 border border-x-border text-x-white placeholder-x-gray rounded-xl px-4 py-3 focus:ring-2 focus:ring-x-blue focus:border-x-blue transition-all duration-200"
                placeholder="Search developers..."
                value={searchTerm}
                onChange={handleSearchChange}
              />
              <svg
                className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-x-gray"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                />
              </svg>
            </div>
          </div>

          <div className="lg:w-72">
            <label htmlFor="skill" className="block text-sm font-medium text-x-white mb-3">
              üè∑Ô∏è Filter by skill
            </label>
            <select
              id="skill"
              className="w-full bg-x-black/60 border border-x-border text-x-white rounded-xl px-4 py-3 focus:ring-2 focus:ring-x-blue focus:border-x-blue transition-all duration-200"
              value={selectedSkill}
              onChange={handleSkillChange}
            >
              <option value="">All skills</option>
              {allSkills.map((skill) => (
                <option key={skill} value={skill}>
                  {skill}
                </option>
              ))}
            </select>
          </div>

          {(searchTerm || selectedSkill) && (
            <div className="flex items-end">
              <button
                onClick={clearFilters}
                className="btn-outline px-6 py-3 whitespace-nowrap hover:scale-105 transform transition-all duration-200"
              >
                ‚ú® Clear Filters
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Results */}
      {loading ? (
        <LoadingSpinner />
      ) : error ? (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg">
          {error}
        </div>
      ) : users.length === 0 ? (
        <div className="text-center py-12">
          <svg
            className="w-12 h-12 text-gray-400 mx-auto mb-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
            />
          </svg>
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            No developers found
          </h3>
          <p className="text-gray-600">
            {searchTerm || selectedSkill
              ? "Try adjusting your search criteria or clear the filters."
              : "No developers have joined the community yet."}
          </p>
        </div>
      ) : (
        <>
          <div className="mb-4">
            <p className="text-gray-600">
              Found {users.length} developer{users.length !== 1 ? "s" : ""}
              {searchTerm && ` matching "${searchTerm}"`}
              {selectedSkill && ` with skill "${selectedSkill}"`}
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {users.map((user) => (
              <div
                key={user._id}
                className="card p-6 hover:shadow-lg transition-shadow"
              >
                <div className="flex items-center mb-4">
                  <div className="bg-primary-600 text-white w-12 h-12 rounded-full flex items-center justify-center text-lg font-bold mr-4">
                    {user.displayName?.charAt(0).toUpperCase() ||
                      user.username.charAt(0).toUpperCase()}
                  </div>
                  <div>
                    <h3 className="font-semibold text-gray-900">
                      {user.displayName || user.username}
                    </h3>
                    <p className="text-sm text-gray-600">@{user.username}</p>
                  </div>
                </div>

                {user.bio && (
                  <p className="text-gray-700 text-sm mb-4 line-clamp-3">
                    {user.bio}
                  </p>
                )}

                {user.skills && user.skills.length > 0 && (
                  <div className="mb-4">
                    <div className="flex flex-wrap gap-1">
                      {user.skills.slice(0, 3).map((skill, index) => (
                        <span
                          key={index}
                          className="bg-primary-100 text-primary-800 px-2 py-1 rounded-full text-xs font-medium"
                        >
                          {skill}
                        </span>
                      ))}
                      {user.skills.length > 3 && (
                        <span className="text-xs text-gray-500 px-2 py-1">
                          +{user.skills.length - 3} more
                        </span>
                      )}
                    </div>
                  </div>
                )}

                <div className="flex justify-between items-center pt-4 border-t border-gray-200">
                  <span className="text-sm text-gray-500">
                    Joined{" "}
                    {new Date(user.createdAt).toLocaleDateString("en-US", {
                      year: "numeric",
                      month: "short",
                    })}
                  </span>

                  <div className="flex space-x-2">
                    {user.githubLink && (
                      <a
                        href={user.githubLink}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-gray-400 hover:text-gray-600 transition-colors"
                        title="GitHub Profile"
                      >
                        <svg
                          className="w-5 h-5"
                          fill="currentColor"
                          viewBox="0 0 20 20"
                        >
                          <path
                            fillRule="evenodd"
                            d="M10 0C4.477 0 0 4.484 0 10.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0110 4.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.203 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.942.359.31.678.921.678 1.856 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0020 10.017C20 4.484 15.522 0 10 0z"
                            clipRule="evenodd"
                          />
                        </svg>
                      </a>
                    )}

                    <Link
                      to={`/profile/${user.username}`}
                      className="btn-primary text-sm"
                    >
                      View Profile
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Explore;
