import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import ShimmerEffect from "../components/ShimmerEffect";
import LoadingSpinner from "../components/LoadingSpinner";
import PostCard from "../components/PostCard";

const Feed = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [showEndMessage, setShowEndMessage] = useState(false);
  const [endAnimationLoading, setEndAnimationLoading] = useState(false);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async (pageNum = 1) => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/posts?page=${pageNum}&limit=10`);

      if (pageNum === 1) {
        setPosts(response.data.posts);
      } else {
        setPosts((prev) => [...prev, ...response.data.posts]);
      }

      setHasMore(
        response.data.pagination.current < response.data.pagination.pages
      );
      setPage(pageNum);
    } catch (error) {
      setError("Failed to fetch posts");
      console.error("Fetch posts error:", error);
    } finally {
      setLoading(false);
    }
  };

  const loadMore = () => {
    if (!loading && hasMore) {
      fetchPosts(page + 1);
    } else if (!hasMore && !userReachedEnd) {
      // User clicked "Load More" but there are no more posts
      setUserReachedEnd(true);
    }
  };

  const handlePostUpdate = (updatedPost) => {
    setPosts(
      posts.map((post) => (post._id === updatedPost._id ? updatedPost : post))
    );
  };

  const handlePostDelete = (deletedPostId) => {
    setPosts(posts.filter((post) => post._id !== deletedPostId));
  };

  useEffect(() => {
    if (!hasMore && posts.length > 0 && userReachedEnd && !showEndMessage && !endAnimationLoading) {
      setEndAnimationLoading(true);
      const timer = setTimeout(() => {
        setEndAnimationLoading(false);
        setShowEndMessage(true);
      }, 2000);

      return () => clearTimeout(timer);
    }
  }, [hasMore, posts.length, userReachedEnd, showEndMessage, endAnimationLoading]);

  if (loading && posts.length === 0) {
    return <ShimmerEffect type="feed" />;
  }

  return (
    <div className="w-full max-w-2xl mx-auto py-2 sm:py-4 lg:py-8 px-3 sm:px-4 lg:px-4">
      <div className="flex flex-row justify-between items-center mb-4 sm:mb-6 lg:mb-8 gap-2 sm:gap-4">
        <h1 className="text-xl sm:text-2xl lg:text-3xl font-bold bg-gradient-to-r from-x-blue via-purple-500 to-x-green bg-[length:200%_auto] bg-clip-text text-transparent animate-color-cycle hover:animate-gradient-x hover:scale-110 transform transition-all duration-500 cursor-default drop-shadow-lg">
          Feed
        </h1>
        <div className="flex items-center gap-2">
          <Link
            to="/create-post"
            className="hidden sm:inline font-bold text-base sm:text-lg px-5 py-2 rounded-full transition-all duration-300 shadow-lg bg-gradient-to-r from-amber-400 via-yellow-400 to-orange-400 text-[#222] focus:outline-none hover:scale-105 no-underline"
          >
            Create 🖋️
          </Link>
          <span className="inline sm:hidden font-bold text-base cursor-default bg-gradient-to-r from-x-blue via-purple-500 to-x-green bg-[length:200%_auto] bg-clip-text text-transparent animate-color-cycle hover:animate-gradient-x hover:scale-110">
            Create📄
          </span>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-3 sm:px-4 py-2 sm:py-3 rounded-lg mb-4 sm:mb-6 text-sm sm:text-base">
          {error}
        </div>
      )}

      {posts.length === 0 && !loading ? (
        <div className="text-center py-8 sm:py-12 px-4">
          <svg
            className="w-10 h-10 sm:w-12 sm:h-12 text-gray-400 mx-auto mb-3 sm:mb-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-1l-4 4z"
            />
          </svg>
          <h3 className="text-base sm:text-lg font-medium text-gray-900 mb-2">
            No posts yet
          </h3>
          <p className="text-sm sm:text-base text-gray-600 mb-3 sm:mb-4">
            Be the first to share something with the community!
          </p>
          <Link
            to="/create-post"
            className="btn-primary text-sm sm:text-base px-4 py-2 sm:px-6 sm:py-3"
          >
            Create First Post
          </Link>
        </div>
      ) : (
        <div className="space-y-4 sm:space-y-6">
          {posts.map((post, index) => (
            <div
              key={post._id}
              className="transition-all duration-300 ease-in-out"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <PostCard
                key={post._id}
                post={post}
                onUpdate={handlePostUpdate}
                onDelete={handlePostDelete}
              />
            </div>
          ))}

          {hasMore && (
            <div className="text-center py-3 sm:py-4">
              <button
                onClick={loadMore}
                disabled={loading}
                className="btn-outline disabled:opacity-50 text-sm sm:text-base px-4 py-2 sm:px-6 sm:py-3"
              >
                {loading ? (
                  <>
                    <LoadingSpinner size="small" className="mr-2" />
                    Loading...
                  </>
                ) : (
                  "Load More"
                )}
              </button>
            </div>
          )}

          {!hasMore && !userReachedEnd && (
            <div className="text-center py-3 sm:py-4">
              <button
                onClick={loadMore}
                className="btn-outline text-sm sm:text-base px-4 py-2 sm:px-6 sm:py-3"
              >
                View End
              </button>
            </div>
          )}

          {!hasMore && posts.length > 0 && userReachedEnd && (
            <div className="text-center py-8 sm:py-12">
              <div className="flex flex-col items-center space-y-4">
                {endAnimationLoading && (
                  <>
                    {/* Loading Animation */}
                    <div className="relative w-16 h-16 mb-4">
                      <div className="absolute top-0 left-0 w-full h-full">
                        <div className="w-4 h-4 bg-blue-500 rounded-full animate-bounce absolute top-0 left-0 animation-delay-0"></div>
                        <div className="w-4 h-4 bg-purple-500 rounded-full animate-bounce absolute top-0 right-0 animation-delay-150"></div>
                        <div className="w-4 h-4 bg-green-500 rounded-full animate-bounce absolute bottom-0 left-0 animation-delay-300"></div>
                        <div className="w-4 h-4 bg-yellow-500 rounded-full animate-bounce absolute bottom-0 right-0 animation-delay-450"></div>
                      </div>
                    </div>
                    <p className="text-sm text-gray-400">Loading...</p>
                  </>
                )}
                
                {showEndMessage && (
                  /* End of feed message */
                  <div className="text-center animate-fade-in">
                    <h3 className="text-lg font-semibold text-gray-300 mb-2">
                      🎉 You've reached the end!
                    </h3>
                    <p className="text-sm text-gray-500">
                      No more posts to show. Come back later for fresh content!
                    </p>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Feed;
