import React from "react";

const ShimmerEffect = ({ type = "feed" }) => {
  // Shimmer animation class
  const shimmerClass = "animate-shimmer rounded";

  const renderFeedShimmer = () => (
    <div className="max-w-2xl mx-auto py-4 lg:py-8 px-2 lg:px-4 space-y-6">
      {/* Header shimmer */}
      <div className="flex justify-between items-center mb-6 lg:mb-8">
        <div className={`h-8 w-24 rounded-lg ${shimmerClass}`}></div>
        <div className={`h-10 w-32 rounded-full ${shimmerClass}`}></div>
      </div>

      {/* Post cards shimmer */}
      {[1, 2, 3].map((item) => (
        <div key={item} className="x-card space-y-4">
          {/* User info */}
          <div className="flex items-center space-x-3">
            <div className={`w-12 h-12 rounded-full ${shimmerClass}`}></div>
            <div className="space-y-2">
              <div className={`h-4 w-32 rounded ${shimmerClass}`}></div>
              <div className={`h-3 w-24 rounded ${shimmerClass}`}></div>
            </div>
          </div>
          
          {/* Post content */}
          <div className="space-y-3">
            <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
            <div className={`h-4 w-3/4 rounded ${shimmerClass}`}></div>
            <div className={`h-4 w-1/2 rounded ${shimmerClass}`}></div>
          </div>
          
          {/* Code block shimmer (sometimes) */}
          {item === 2 && (
            <div className={`h-32 w-full rounded-lg ${shimmerClass}`}></div>
          )}
          
          {/* Action buttons */}
          <div className="flex items-center space-x-6 pt-4">
            <div className={`h-6 w-16 rounded ${shimmerClass}`}></div>
            <div className={`h-6 w-20 rounded ${shimmerClass}`}></div>
            <div className={`h-6 w-18 rounded ${shimmerClass}`}></div>
          </div>
        </div>
      ))}
    </div>
  );

  const renderProfileShimmer = () => (
    <div className="max-w-2xl mx-auto py-4 lg:py-8 px-2 lg:px-4 space-y-8">
      {/* Profile header */}
      <div className="x-card space-y-6">
        <div className="flex items-start space-x-6">
          <div className={`w-24 h-24 rounded-full ${shimmerClass}`}></div>
          <div className="flex-1 space-y-4">
            <div className={`h-8 w-48 rounded ${shimmerClass}`}></div>
            <div className={`h-4 w-32 rounded ${shimmerClass}`}></div>
            <div className="space-y-2">
              <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
              <div className={`h-4 w-2/3 rounded ${shimmerClass}`}></div>
            </div>
          </div>
        </div>
        
        {/* Stats */}
        <div className="flex justify-around">
          {[1, 2, 3].map((stat) => (
            <div key={stat} className="text-center space-y-2">
              <div className={`h-6 w-12 rounded mx-auto ${shimmerClass}`}></div>
              <div className={`h-4 w-16 rounded mx-auto ${shimmerClass}`}></div>
            </div>
          ))}
        </div>
      </div>

      {/* Posts shimmer */}
      {[1, 2].map((item) => (
        <div key={item} className="x-card space-y-4">
          <div className="space-y-3">
            <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
            <div className={`h-4 w-3/4 rounded ${shimmerClass}`}></div>
          </div>
          <div className="flex items-center space-x-6">
            <div className={`h-6 w-16 rounded ${shimmerClass}`}></div>
            <div className={`h-6 w-20 rounded ${shimmerClass}`}></div>
          </div>
        </div>
      ))}
    </div>
  );

  const renderPostDetailShimmer = () => (
    <div className="max-w-2xl mx-auto py-4 lg:py-8 px-2 lg:px-4 space-y-6">
      {/* Back button */}
      <div className={`h-10 w-24 rounded-full ${shimmerClass}`}></div>
      
      {/* Post detail */}
      <div className="x-card space-y-6">
        {/* User info */}
        <div className="flex items-center space-x-3">
          <div className={`w-12 h-12 rounded-full ${shimmerClass}`}></div>
          <div className="space-y-2">
            <div className={`h-4 w-32 rounded ${shimmerClass}`}></div>
            <div className={`h-3 w-24 rounded ${shimmerClass}`}></div>
          </div>
        </div>
        
        {/* Post content */}
        <div className="space-y-4">
          <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
          <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
          <div className={`h-4 w-3/4 rounded ${shimmerClass}`}></div>
          <div className={`h-40 w-full rounded-lg ${shimmerClass}`}></div>
        </div>
        
        {/* Actions */}
        <div className="flex items-center space-x-6">
          <div className={`h-6 w-16 rounded ${shimmerClass}`}></div>
          <div className={`h-6 w-20 rounded ${shimmerClass}`}></div>
        </div>
      </div>
      
      {/* Comments section */}
      <div className="space-y-4">
        <div className={`h-6 w-24 rounded ${shimmerClass}`}></div>
        {[1, 2].map((comment) => (
          <div key={comment} className="flex space-x-3">
            <div className={`w-8 h-8 rounded-full flex-shrink-0 ${shimmerClass}`}></div>
            <div className="flex-1 space-y-2">
              <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
              <div className={`h-4 w-2/3 rounded ${shimmerClass}`}></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  switch (type) {
    case 'profile':
      return renderProfileShimmer();
    case 'post-detail':
      return renderPostDetailShimmer();
    case 'feed':
    default:
      return renderFeedShimmer();
  }
};

export default ShimmerEffect;
