import React, { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import axios from "axios";

const SettingPage = () => {
  const { user, logout, updateUser } = useAuth();
  const [form, setForm] = useState({
    username: user?.username || "",
    email: user?.email || "",
    password: "",
    notifications: user?.notifications ?? true,
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    setForm({
      username: user?.username || "",
      email: user?.email || "",
      password: "",
      notifications: user?.notifications ?? true,
    });
  }, [user]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSave = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");
    try {
      const res = await axios.put(`/api/users/${user._id}/settings`, {
        username: form.username,
        email: form.email,
        password: form.password || undefined,
        notifications: form.notifications,
      });
      updateUser(res.data);
      setSuccess("Settings updated successfully!");
      setForm((prev) => ({ ...prev, password: "" }));
    } catch (err) {
      setError(err.response?.data?.message || "Failed to update settings");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm("Are you sure you want to delete your account? This cannot be undone.")) return;
    setLoading(true);
    setError("");
    try {
      await axios.delete(`/api/users/${user._id}`);
      logout();
    } catch (err) {
      setError("Failed to delete account");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="w-full max-w-2xl mx-auto py-2 sm:py-4 lg:py-8 px-3 sm:px-4 lg:px-4">
      <div className="bg-gradient-to-br from-x-dark/90 to-x-dark/60 backdrop-blur-sm border border-x-border/50 rounded-2xl sm:rounded-3xl p-6 sm:p-10 shadow-xl">
        <h1 className="text-2xl sm:text-3xl lg:text-4xl font-bold bg-gradient-to-r from-x-blue via-purple-500 to-x-green bg-[length:200%_auto] bg-clip-text text-transparent animate-color-cycle hover:animate-gradient-x hover:scale-110 transform transition-all duration-500 cursor-default drop-shadow-lg mb-6 text-center">
          Settings
        </h1>
        <form className="space-y-8 mt-4" onSubmit={handleSave}>
          {/* Account Section */}
          <section>
            <h2 className="text-lg font-semibold text-x-white mb-3 tracking-wide flex items-center gap-2">
              <span className="inline-block w-2 h-2 bg-x-blue rounded-full"></span> Account
            </h2>
            <div className="bg-x-dark/40 border border-x-border/30 rounded-xl p-5 flex flex-col gap-5 shadow-inner">
              <div className="flex flex-col md:flex-row md:items-center gap-3">
                <label className="text-x-gray w-32 font-medium">Username</label>
                <input type="text" name="username" value={form.username} onChange={handleChange} className="bg-x-dark border border-x-blue/30 rounded-lg px-4 py-2 text-x-white focus:outline-none focus:ring-2 focus:ring-x-blue/60 transition-all" placeholder="Change username" />
              </div>
              <div className="flex flex-col md:flex-row md:items-center gap-3">
                <label className="text-x-gray w-32 font-medium">Email</label>
                <input type="email" name="email" value={form.email} onChange={handleChange} className="bg-x-dark border border-x-blue/30 rounded-lg px-4 py-2 text-x-white focus:outline-none focus:ring-2 focus:ring-x-blue/60 transition-all" placeholder="Change email" />
              </div>
              <div className="flex flex-col md:flex-row md:items-center gap-3">
                <label className="text-x-gray w-32 font-medium">Password</label>
                <input type="password" name="password" value={form.password} onChange={handleChange} className="bg-x-dark border border-x-blue/30 rounded-lg px-4 py-2 text-x-white focus:outline-none focus:ring-2 focus:ring-x-blue/60 transition-all" placeholder="Change password" autoComplete="new-password" />
              </div>
              <button type="submit" disabled={loading} className="mt-2 bg-gradient-to-r from-x-blue to-x-darker hover:from-x-blue/80 hover:to-x-darker/80 text-white font-bold py-2 px-8 rounded-lg shadow-lg transition-all duration-200 disabled:opacity-60">
                {loading ? "Saving..." : "Save Changes"}
              </button>
              {success && <div className="text-green-400 text-sm mt-2">{success}</div>}
              {error && <div className="text-red-400 text-sm mt-2">{error}</div>}
            </div>
          </section>
          {/* Notifications Section */}
          <section>
            <h2 className="text-lg font-semibold text-x-white mb-3 tracking-wide flex items-center gap-2">
              <span className="inline-block w-2 h-2 bg-x-blue rounded-full"></span> Notifications
            </h2>
            <div className="bg-x-dark/40 border border-x-border/30 rounded-xl p-5 flex flex-col gap-5 shadow-inner">
              <div className="flex items-center justify-between">
                <span className="text-x-gray font-medium">Enable Notifications</span>
                <label className="inline-flex items-center cursor-pointer">
                  <input type="checkbox" name="notifications" checked={form.notifications} onChange={handleChange} className="sr-only peer" />
                  <div className="w-11 h-6 bg-x-dark peer-focus:outline-none peer-focus:ring-2 peer-focus:ring-x-blue rounded-full peer peer-checked:bg-x-blue transition-all duration-200"></div>
                  <span className="ml-3 text-x-gray text-sm"></span>
                </label>
              </div>
            </div>
          </section>
        </form>
        {/* Logout & Danger Zone */}
        <section className="mt-8">
          <div className="flex flex-col md:flex-row gap-4 items-center justify-between">
            <button onClick={logout} className="bg-x-dark/40 hover:bg-x-blue/40 text-x-white font-semibold py-2 px-6 rounded-lg transition-all border border-x-blue/30">
              Logout
            </button>
            <button onClick={handleDelete} disabled={loading} className="bg-gradient-to-r from-red-600 to-red-800 hover:from-red-700 hover:to-red-900 text-white font-bold py-2 px-8 rounded-lg shadow-lg transition-all duration-200 disabled:opacity-60">
              {loading ? "Deleting..." : "Delete Account"}
            </button>
          </div>
        </section>
      </div>
    </div>
  );
};

export default SettingPage;
