import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const BottomNav = () => {
  const { user } = useAuth();
  const location = useLocation();
  const [isVisible, setIsVisible] = useState(false);

  // Handle scroll to show/hide bottom nav
  useEffect(() => {
    const handleScroll = () => {
      const scrollTop =
        window.pageYOffset || document.documentElement.scrollTop;
      // Show nav after scrolling 100px down
      setIsVisible(scrollTop > 100);
    };

    // Throttle scroll events for better performance
    let timeoutId;
    const throttledHandleScroll = () => {
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
      timeoutId = setTimeout(handleScroll, 10);
    };

    window.addEventListener("scroll", throttledHandleScroll, { passive: true });
    return () => {
      window.removeEventListener("scroll", throttledHandleScroll);
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
    };
  }, []);

  // Don't render if user is not logged in
  if (!user) return null;

  const navItems = [
    {
      path: "/feed",
      icon: "🏠",
      label: "Home",
      activeIcon: "🏡",
    },
    {
      path: "/explore",
      icon: "🔍",
      label: "Explore",
      activeIcon: "🔎",
    },
    {
      path: "/create-post",
      icon: "➕",
      label: "Post",
      activeIcon: "✨",
      special: true,
    },
    {
      path: `/profile/${user?.username}`,
      icon: "👤",
      label: "Profile",
      activeIcon: "👥",
    },
  ];

  const isActive = (path) => {
    if (path === "/feed" && location.pathname === "/") return true;
    return location.pathname === path || location.pathname.startsWith(path);
  };

  return (
    <div
      className={`lg:hidden fixed bottom-4 left-1/2 transform -translate-x-1/2 z-50 transition-all duration-500 ease-out ${
        isVisible
          ? "translate-y-0 opacity-100 scale-100"
          : "translate-y-16 opacity-0 scale-75 pointer-events-none"
      }`}
    >
      <div className="bottom-nav-pill rounded-full px-6 py-4">
        <div className="flex items-center space-x-2">
          {navItems.map((item) => {
            const active = isActive(item.path);
            return (
              <Link
                key={item.path}
                to={item.path}
                className={`
                  bottom-nav-item flex flex-col items-center justify-center rounded-full relative overflow-hidden
                  ${item.special ? "w-14 h-14 bottom-nav-special" : "w-12 h-12"}
                  ${active && !item.special ? "active" : ""}
                `}
                title={item.label}
              >
                <span
                  className={`
                    text-lg transition-all duration-300
                    ${
                      item.special
                        ? "text-white"
                        : active
                        ? "text-x-blue"
                        : "text-x-gray hover:text-x-white"
                    }
                    ${active ? "scale-110" : ""}
                  `}
                >
                  {active && !item.special ? item.activeIcon : item.icon}
                </span>

                {/* Active indicator dot */}
                {active && !item.special && (
                  <div className="absolute -bottom-1 w-1 h-1 bg-x-blue rounded-full animate-pulse" />
                )}

                {/* Ripple effect on touch */}
                <div className="absolute inset-0 rounded-full bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-200" />
              </Link>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default BottomNav;
