import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";

// Feature Card Component with hover animations
const FeatureCard = ({ icon, title, description, details, delay = 0 }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const cardRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setTimeout(() => setIsVisible(true), delay);
        }
      },
      { threshold: 0.1 }
    );

    if (cardRef.current) {
      observer.observe(cardRef.current);
    }

    return () => observer.disconnect();
  }, [delay]);

  return (
    <div
      ref={cardRef}
      className={`group relative bg-x-dark border border-x-border rounded-2xl p-8 transition-all duration-700 transform hover:scale-105 hover:shadow-2xl hover:shadow-x-blue/20 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
      }`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Animated Background Gradient */}
      <div className="absolute inset-0 bg-gradient-to-br from-x-blue/5 to-x-green/5 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
      
      {/* Icon */}
      <div className="relative mb-6">
        <div className={`w-16 h-16 bg-gradient-to-r from-x-blue to-x-green rounded-xl flex items-center justify-center text-2xl transform transition-transform duration-300 ${isHovered ? 'rotate-12 scale-110' : ''}`}>
          {icon}
        </div>
        {/* Floating particles around icon */}
        {isHovered && (
          <div className="absolute inset-0 pointer-events-none">
            <div className="absolute -top-2 -right-2 w-3 h-3 bg-x-blue rounded-full animate-ping"></div>
            <div className="absolute -bottom-2 -left-2 w-2 h-2 bg-x-green rounded-full animate-ping" style={{animationDelay: '300ms'}}></div>
          </div>
        )}
      </div>

      {/* Content */}
      <div className="relative z-10">
        <h3 className="text-xl font-bold text-x-white mb-3 group-hover:text-x-blue transition-colors duration-300">
          {title}
        </h3>
        <p className="text-x-gray mb-4 leading-relaxed">
          {description}
        </p>
        
        {/* Expandable Details */}
        <div className={`overflow-hidden transition-all duration-500 ${isHovered ? 'max-h-40 opacity-100' : 'max-h-0 opacity-0'}`}>
          <ul className="text-sm text-x-gray space-y-2">
            {details?.map((detail, index) => (
              <li key={index} className="flex items-center">
                <span className="w-1.5 h-1.5 bg-x-blue rounded-full mr-3"></span>
                {detail}
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Hover Border Effect */}
      <div className="absolute inset-0 rounded-2xl bg-gradient-to-r from-x-blue via-x-green to-x-blue opacity-0 group-hover:opacity-20 transition-opacity duration-500 -z-10 blur-xl"></div>
    </div>
  );
};

// Statistics Counter Component
const StatCounter = ({ target, label, delay = 0 }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const counterRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
          setTimeout(() => {
            let start = 0;
            const duration = 2000;
            const increment = target / (duration / 16);
            
            const timer = setInterval(() => {
              start += increment;
              if (start >= target) {
                setCount(target);
                clearInterval(timer);
              } else {
                setCount(Math.floor(start));
              }
            }, 16);
          }, delay);
        }
      },
      { threshold: 0.1 }
    );

    if (counterRef.current) {
      observer.observe(counterRef.current);
    }

    return () => observer.disconnect();
  }, [target, delay, isVisible]);

  return (
    <div ref={counterRef} className="text-center group">
      <div className="relative">
        <div className="text-4xl md:text-5xl font-bold text-x-white mb-2 group-hover:scale-110 transition-transform duration-300">
          {count.toLocaleString()}
          {target >= 1000 && count === target && (
            <span className="text-x-blue">+</span>
          )}
        </div>
        <div className="absolute -inset-2 bg-gradient-to-r from-x-blue/20 to-x-green/20 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300 -z-10 blur"></div>
      </div>
      <p className="text-x-gray font-medium">{label}</p>
    </div>
  );
};

// Interactive Demo Component
const InteractiveDemo = () => {
  const [activeDemo, setActiveDemo] = useState('code');
  
  const demos = {
    code: {
      title: "Smart Code Sharing",
      content: (
        <div className="bg-gray-900 rounded-lg p-4 text-sm font-mono">
          <div className="flex items-center mb-3">
            <div className="flex space-x-2">
              <div className="w-3 h-3 bg-red-500 rounded-full"></div>
              <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
              <div className="w-3 h-3 bg-green-500 rounded-full"></div>
            </div>
            <span className="ml-4 text-gray-400">main.js</span>
          </div>
          <div className="text-green-400">
            <span className="text-blue-400">function</span> 
            <span className="text-yellow-400"> createPost</span>
            <span className="text-gray-300">()</span> 
            <span className="text-gray-300">{'{'}</span>
          </div>
          <div className="pl-4 text-gray-300">
            <span className="text-blue-400">const</span> post = 
            <span className="text-orange-400"> "Hello DevMate!"</span>
          </div>
          <div className="pl-4 text-gray-300">
            <span className="text-purple-400">return</span> post
          </div>
          <div className="text-gray-300">{'}'}</div>
        </div>
      )
    },
    chat: {
      title: "Real-time Collaboration",
      content: (
        <div className="space-y-3">
          <div className="flex items-start space-x-3">
            <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-sm text-white">A</div>
            <div className="bg-blue-100 text-gray-800 rounded-lg px-3 py-2 max-w-xs">
              Love this implementation! 🚀
            </div>
          </div>
          <div className="flex items-start space-x-3 justify-end">
            <div className="bg-green-100 text-gray-800 rounded-lg px-3 py-2 max-w-xs">
              Thanks! Want to collaborate?
            </div>
            <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center text-sm text-white">B</div>
          </div>
          <div className="flex items-center space-x-2 text-gray-500 text-sm">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            <span>Alex is typing...</span>
          </div>
        </div>
      )
    },
    network: {
      title: "Developer Network",
      content: (
        <div className="relative">
          <div className="grid grid-cols-3 gap-4 mb-4">
            {[1,2,3,4,5,6].map(i => (
              <div key={i} className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center text-white text-sm animate-pulse">
                {String.fromCharCode(64 + i)}
              </div>
            ))}
          </div>
          <div className="text-center text-gray-600 text-sm">
            Connected developers worldwide
          </div>
        </div>
      )
    }
  };

  return (
    <div className="bg-x-dark rounded-2xl p-8 shadow-xl border border-x-border">
      <div className="flex space-x-4 mb-6">
        {Object.entries(demos).map(([key, demo]) => (
          <button
            key={key}
            onClick={() => setActiveDemo(key)}
            className={`px-4 py-2 rounded-lg font-medium transition-all duration-300 ${
              activeDemo === key 
                ? 'bg-x-blue text-white shadow-lg' 
                : 'bg-x-darker text-x-gray hover:bg-x-border hover:text-x-white'
            }`}
          >
            {demo.title}
          </button>
        ))}
      </div>
      
      <div className="min-h-[200px] transition-all duration-500">
        {demos[activeDemo].content}
      </div>
    </div>
  );
};

// Main Features Component
const Features = () => {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    // Scroll to top when component mounts
    window.scrollTo(0, 0);
    setIsLoaded(true);
  }, []);

  const features = [
    {
      icon: "💻",
      title: "Smart Code Sharing",
      description: "Share your code with beautiful syntax highlighting and interactive features that make collaboration seamless.",
      details: [
        "Real-time syntax highlighting",
        "Multiple language support",
        "Collaborative editing",
        "Version control integration"
      ]
    },
    {
      icon: "🚀",
      title: "Performance Optimized",
      description: "Lightning-fast performance with optimized algorithms and modern architecture for the best user experience.",
      details: [
        "Sub-second load times",
        "Optimized for mobile",
        "Progressive web app",
        "Offline capabilities"
      ]
    },
    {
      icon: "🔒",
      title: "Privacy First",
      description: "Your data is protected with enterprise-grade security and privacy controls you can trust.",
      details: [
        "End-to-end encryption",
        "GDPR compliant",
        "No data selling",
        "Transparent policies"
      ]
    },
    {
      icon: "🌐",
      title: "Global Community",
      description: "Connect with developers worldwide and build meaningful professional relationships.",
      details: [
        "Developer matching",
        "Global events",
        "Mentorship programs",
        "Career opportunities"
      ]
    },
    {
      icon: "🎨",
      title: "Beautiful Design",
      description: "Enjoy a stunning, modern interface designed specifically for developers by developers.",
      details: [
        "Dark/light themes",
        "Customizable layouts",
        "Responsive design",
        "Accessibility focused"
      ]
    },
    {
      icon: "🔧",
      title: "Developer Tools",
      description: "Integrated tools and features that enhance your development workflow and productivity.",
      details: [
        "Code snippets",
        "API testing",
        "Documentation tools",
        "Integration support"
      ]
    }
  ];

  const stats = [
    { target: 50000, label: "Active Developers" },
    { target: 1000000, label: "Lines of Code Shared" },
    { target: 150, label: "Countries Represented" },
    { target: 25000, label: "Projects Created" }
  ];

  return (
    <div className="min-h-screen bg-x-black text-x-white">
      {/* Logo/Navigation Header */}
      <div className="relative z-50 bg-x-black/90 backdrop-blur-sm border-b border-x-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <Link 
            to="/" 
            className="flex items-center space-x-3 hover:opacity-80 transition-opacity duration-300 w-fit"
          >
            <div className="w-10 h-10 bg-gradient-to-r from-x-blue to-x-green rounded-full flex items-center justify-center">
              <span className="text-white font-bold text-lg">D</span>
            </div>
            <span className="text-2xl font-bold text-x-white">DevMate</span>
          </Link>
        </div>
      </div>

      {/* Hero Section */}
      <section className="relative pt-20 pb-32 overflow-hidden">
        {/* Animated Background */}
        <div className="absolute inset-0">
          <div className="absolute top-0 left-0 w-72 h-72 bg-x-blue/20 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob"></div>
          <div className="absolute top-0 right-0 w-72 h-72 bg-x-green/20 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob" style={{animationDelay: '2s'}}></div>
          <div className="absolute -bottom-8 left-20 w-72 h-72 bg-x-purple/20 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob" style={{animationDelay: '4s'}}></div>
        </div>

        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className={`text-center transform transition-all duration-1000 ${isLoaded ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'}`}>
            <h1 className="text-5xl md:text-7xl font-bold text-x-white mb-6">
              Powerful
              <span className="block bg-gradient-to-r from-x-blue to-x-green bg-clip-text text-transparent">
                Features
              </span>
            </h1>
            <p className="text-xl md:text-2xl text-x-gray mb-8 max-w-3xl mx-auto leading-relaxed">
              Discover the tools and features that make DevMate the ultimate platform for developers to connect, collaborate, and create amazing things together.
            </p>
            
            {/* CTA Buttons */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link 
                to="/register" 
                className="px-8 py-4 bg-gradient-to-r from-x-blue to-x-green text-white font-semibold rounded-xl hover:shadow-lg hover:scale-105 transition-all duration-300"
              >
                Get Started Free
              </Link>
              <Link 
                to="/explore" 
                className="px-8 py-4 bg-x-dark text-x-white font-semibold rounded-xl border-2 border-x-border hover:border-x-blue hover:text-x-blue transition-all duration-300"
              >
                Explore Platform
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Features Grid */}
      <section className="py-20 relative">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
              Everything You Need
            </h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              From smart code sharing to global networking, DevMate provides all the tools you need to succeed as a developer.
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {features.map((feature, index) => (
              <FeatureCard
                key={index}
                icon={feature.icon}
                title={feature.title}
                description={feature.description}
                details={feature.details}
                delay={index * 100}
              />
            ))}
          </div>
        </div>
      </section>

      {/* Interactive Demo Section */}
      <section className="py-20 bg-gradient-to-r from-blue-600 to-purple-600">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl md:text-5xl font-bold text-white mb-6">
              See DevMate in Action
            </h2>
            <p className="text-xl text-blue-100 max-w-3xl mx-auto">
              Experience the power of DevMate with our interactive demonstrations.
            </p>
          </div>

          <div className="max-w-4xl mx-auto">
            <InteractiveDemo />
          </div>
        </div>
      </section>

      {/* Statistics Section */}
      <section className="py-20 bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl md:text-5xl font-bold text-white mb-6">
              Trusted by Developers
            </h2>
            <p className="text-xl text-gray-400 max-w-3xl mx-auto">
              Join thousands of developers who are already using DevMate to accelerate their careers.
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {stats.map((stat, index) => (
              <StatCounter
                key={index}
                target={stat.target}
                label={stat.label}
                delay={index * 200}
              />
            ))}
          </div>
        </div>
      </section>

      {/* Final CTA Section */}
      <section className="py-20 bg-white">
        <div className="max-w-4xl mx-auto text-center px-4 sm:px-6 lg:px-8">
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
            Ready to Get Started?
          </h2>
          <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">
            Join DevMate today and become part of the most innovative developer community on the planet.
          </p>
          
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link 
              to="/register" 
              className="px-12 py-4 bg-gradient-to-r from-blue-600 to-purple-600 text-white font-semibold rounded-xl hover:shadow-xl hover:scale-105 transition-all duration-300 text-lg"
            >
              Start Your Journey
            </Link>
            <Link 
              to="/contact" 
              className="px-12 py-4 bg-gray-100 text-gray-800 font-semibold rounded-xl hover:bg-gray-200 transition-all duration-300 text-lg"
            >
              Contact Sales
            </Link>
          </div>
        </div>
      </section>

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes blob {
          0%, 100% { 
            transform: translate(0px, 0px) scale(1); 
          }
          33% { 
            transform: translate(30px, -50px) scale(1.1); 
          }
          66% { 
            transform: translate(-20px, 20px) scale(0.9); 
          }
        }
        .animate-blob {
          animation: blob 7s infinite;
        }
      `}</style>
    </div>
  );
};

export default Features;
