import React, { useState } from "react";

const faqs = [
  {
    question: "How do I create an account?",
    answer:
      "Click the Register button on the homepage and fill out the required information. You'll receive a confirmation email to activate your account.",
  },
  {
    question: "How do I reset my password?",
    answer:
      "Go to the Login page and click on 'Forgot Password'. Follow the instructions to reset your password via email.",
  },
  {
    question: "How do I post code or questions?",
    answer:
      "After logging in, use the 'Create Post' button in the navigation bar. You can share code snippets, ask questions, or start discussions.",
  },
  {
    question: "Is my data private?",
    answer:
      "We use enterprise-grade security and never sell your data. You can manage your privacy settings in your profile.",
  },
  {
    question: "How do I contact support?",
    answer:
      "Use the contact form below or email us at support@devmate.com. Our team will respond as soon as possible.",
  },
];

const guidelines = [
  "Be respectful and constructive in all interactions.",
  "No spam, advertising, or self-promotion.",
  "Share only content you have the right to post.",
  "Report inappropriate behavior to moderators.",
  "Help others and contribute positively to the community.",
];

const Support = () => {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [submitted, setSubmitted] = useState(false);
  const [faqOpen, setFaqOpen] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    // Here you would send the form data to your backend or support system
  };

  return (
    <div className="min-h-screen bg-x-black text-x-white py-12 px-4 sm:px-8 max-w-4xl mx-auto">
      <h1 className="text-4xl font-bold mb-8 text-center">Support & Help Center</h1>

      {/* FAQ Section */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-6 text-x-blue">Frequently Asked Questions</h2>
        <div className="space-y-4">
          {faqs.map((faq, idx) => (
            <div key={idx} className="border border-x-border rounded-lg bg-x-dark/60">
              <button
                className="w-full text-left px-6 py-4 focus:outline-none flex justify-between items-center"
                onClick={() => setFaqOpen(faqOpen === idx ? null : idx)}
              >
                <span className="font-medium text-lg">{faq.question}</span>
                <span className="ml-4 text-x-blue text-2xl">{faqOpen === idx ? "-" : "+"}</span>
              </button>
              {faqOpen === idx && (
                <div className="px-6 pb-4 text-x-gray animate-fade-in">
                  {faq.answer}
                </div>
              )}
            </div>
          ))}
        </div>
      </section>

      {/* Contact Support Section */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-6 text-x-blue">Contact Support</h2>
        {submitted ? (
          <div className="bg-x-green/10 border border-x-green text-x-green rounded-lg p-6 text-center font-semibold">
            Thank you for reaching out! Our support team will get back to you soon.
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-6 bg-x-dark/60 border border-x-border rounded-lg p-6">
            <div>
              <label className="block mb-2 text-x-gray font-medium">Name</label>
              <input
                type="text"
                name="name"
                value={form.name}
                onChange={handleChange}
                required
                className="w-full px-4 py-2 rounded bg-x-black border border-x-border text-x-white focus:border-x-blue outline-none"
              />
            </div>
            <div>
              <label className="block mb-2 text-x-gray font-medium">Email</label>
              <input
                type="email"
                name="email"
                value={form.email}
                onChange={handleChange}
                required
                className="w-full px-4 py-2 rounded bg-x-black border border-x-border text-x-white focus:border-x-blue outline-none"
              />
            </div>
            <div>
              <label className="block mb-2 text-x-gray font-medium">Message</label>
              <textarea
                name="message"
                value={form.message}
                onChange={handleChange}
                required
                rows={4}
                className="w-full px-4 py-2 rounded bg-x-black border border-x-border text-x-white focus:border-x-blue outline-none"
              ></textarea>
            </div>
            <button
              type="submit"
              className="w-full bg-x-blue hover:bg-x-blue-hover text-white font-bold py-3 rounded transition-all duration-300 shadow-lg hover:scale-105"
            >
              Send Message
            </button>
            <div className="text-center text-x-gray mt-2">
              Or email us directly at
              <a href="mailto:support@devmate.com" className="text-x-blue underline ml-1">support@devmate.com</a>
            </div>
          </form>
        )}
      </section>

      {/* Community Guidelines Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-6 text-x-blue">Community Guidelines</h2>
        <ul className="list-disc pl-8 space-y-2 text-x-gray">
          {guidelines.map((rule, idx) => (
            <li key={idx}>{rule}</li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default Support;
