import React from "react";
import { Link, useLocation } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const BottomNav = () => {
  const { user } = useAuth();
  const location = useLocation();

  if (!user) return null;

  const navItems = [
    { 
      path: "/feed", 
      icon: "🏠", 
      label: "Home",
      activeIcon: "🏡"
    },
    { 
      path: "/explore", 
      icon: "🔍", 
      label: "Explore",
      activeIcon: "🔎"
    },
    { 
      path: "/create-post", 
      icon: "➕", 
      label: "Post",
      activeIcon: "✨",
      special: true
    },
    { 
      path: `/profile/${user?.username}`, 
      icon: "👤", 
      label: "Profile",
      activeIcon: "👥"
    },
  ];

  const isActive = (path) => {
    if (path === "/feed" && location.pathname === "/") return true;
    return location.pathname === path || location.pathname.startsWith(path);
  };

  return (
    <div className="lg:hidden fixed bottom-4 left-1/2 transform -translate-x-1/2 z-50">
      <div className="bottom-nav-pill rounded-full px-4 py-3">
        <div className="flex items-center space-x-1">
          {navItems.map((item) => {
            const active = isActive(item.path);
            return (
              <Link
                key={item.path}
                to={item.path}
                className={`
                  bottom-nav-item flex flex-col items-center justify-center rounded-full
                  ${item.special 
                    ? 'w-12 h-12 bottom-nav-special' 
                    : 'w-10 h-10 hover:bg-x-darker'
                  }
                  ${active && !item.special ? 'active bg-x-blue/20' : ''}
                `}
                title={item.label}
              >
                <span 
                  className={`
                    text-lg transition-all duration-300
                    ${item.special ? 'text-white' : active ? 'text-x-blue' : 'text-x-gray hover:text-x-white'}
                    ${active ? 'scale-110' : ''}
                  `}
                >
                  {active && !item.special ? item.activeIcon : item.icon}
                </span>
                
                {/* Active indicator dot */}
                {active && !item.special && (
                  <div className="absolute -bottom-1 w-1 h-1 bg-x-blue rounded-full animate-pulse" />
                )}
                
                {/* Ripple effect on touch */}
                <div className="absolute inset-0 rounded-full bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-200" />
              </Link>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default BottomNav;
