import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import LoadingSpinner from "../components/LoadingSpinner";
import PostCard from "../components/PostCard";

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const { username } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();

  const isOwnProfile = user?.username === username;

  useEffect(() => {
    fetchProfile();
  }, [username]);

  const fetchProfile = async () => {
    try {
      const response = await axios.get(`/api/users/${username}`);
      setProfileData(response.data);
    } catch (error) {
      setError(error.response?.data?.message || "Failed to fetch profile");
    } finally {
      setLoading(false);
    }
  };

  const handlePostUpdate = (updatedPost) => {
    setProfileData((prev) => ({
      ...prev,
      posts: prev.posts.map((post) =>
        post._id === updatedPost._id ? updatedPost : post
      ),
    }));
  };

  const handlePostDelete = (deletedPostId) => {
    setProfileData((prev) => ({
      ...prev,
      posts: prev.posts.filter((post) => post._id !== deletedPostId),
    }));
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto py-8">
        <LoadingSpinner />
      </div>
    );
  }

  if (error || !profileData) {
    return (
      <div className="max-w-4xl mx-auto py-8 px-4">
        <div className="text-center">
          <svg
            className="w-12 h-12 text-gray-400 mx-auto mb-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
            />
          </svg>
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            User not found
          </h3>
          <p className="text-gray-600 mb-4">
            {error || "The user you're looking for doesn't exist."}
          </p>
          <Link to="/explore" className="btn-primary">
            Explore Users
          </Link>
        </div>
      </div>
    );
  }

  const { user: profileUser, posts } = profileData;

  return (
    <div className="max-w-4xl mx-auto py-8 px-4">
      {/* Profile Header */}
      <div className="card p-8 mb-8">
        <div className="flex flex-col md:flex-row items-start md:items-center justify-between">
          <div className="flex items-center mb-4 md:mb-0">
            <div className="bg-primary-600 text-white w-20 h-20 rounded-full flex items-center justify-center text-2xl font-bold mr-6">
              {profileUser.displayName?.charAt(0).toUpperCase() ||
                profileUser.username.charAt(0).toUpperCase()}
            </div>
            <div>
              <h1 className="text-3xl font-bold text-gray-900">
                {profileUser.displayName || profileUser.username}
              </h1>
              <p className="text-lg text-gray-600">@{profileUser.username}</p>
              <p className="text-sm text-gray-500">
                Joined{" "}
                {new Date(profileUser.createdAt).toLocaleDateString("en-US", {
                  year: "numeric",
                  month: "long",
                })}
              </p>
            </div>
          </div>

          {isOwnProfile && (
            <Link to="/edit-profile" className="btn-primary">
              Edit Profile
            </Link>
          )}
        </div>

        {/* Bio */}
        {profileUser.bio && (
          <div className="mt-6">
            <p className="text-gray-700 leading-relaxed">{profileUser.bio}</p>
          </div>
        )}

        {/* Skills */}
        {profileUser.skills && profileUser.skills.length > 0 && (
          <div className="mt-6">
            <h3 className="text-sm font-semibold text-gray-900 mb-3">Skills</h3>
            <div className="flex flex-wrap gap-2">
              {profileUser.skills.map((skill, index) => (
                <span
                  key={index}
                  className="bg-primary-100 text-primary-800 px-3 py-1 rounded-full text-sm font-medium"
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>
        )}

        {/* GitHub Link */}
        {profileUser.githubLink && (
          <div className="mt-6">
            <a
              href={profileUser.githubLink}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center text-gray-700 hover:text-primary-600 transition-colors"
            >
              <svg
                className="w-5 h-5 mr-2"
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M10 0C4.477 0 0 4.484 0 10.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0110 4.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.203 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.942.359.31.678.921.678 1.856 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0020 10.017C20 4.484 15.522 0 10 0z"
                  clipRule="evenodd"
                />
              </svg>
              GitHub Profile
            </a>
          </div>
        )}

        {/* Stats */}
        <div className="mt-6 pt-6 border-t border-gray-200">
          <div className="flex space-x-8">
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">
                {posts.length}
              </div>
              <div className="text-sm text-gray-600">Posts</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">
                {posts.reduce((total, post) => total + post.likes.length, 0)}
              </div>
              <div className="text-sm text-gray-600">Likes Received</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-900">
                {posts.reduce((total, post) => total + post.comments.length, 0)}
              </div>
              <div className="text-sm text-gray-600">Comments Received</div>
            </div>
          </div>
        </div>
      </div>

      {/* Posts Section */}
      <div>
        <h2 className="text-2xl font-bold text-gray-900 mb-6">
          {isOwnProfile
            ? "Your Posts"
            : `${profileUser.displayName || profileUser.username}'s Posts`}
        </h2>

        {posts.length === 0 ? (
          <div className="text-center py-12">
            <svg
              className="w-12 h-12 text-gray-400 mx-auto mb-4"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-1l-4 4z"
              />
            </svg>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No posts yet
            </h3>
            <p className="text-gray-600 mb-4">
              {isOwnProfile
                ? "You haven't created any posts yet. Share your first post with the community!"
                : "This user hasn't posted anything yet."}
            </p>
            {isOwnProfile && (
              <Link to="/create-post" className="btn-primary">
                Create First Post
              </Link>
            )}
          </div>
        ) : (
          <div className="space-y-6">
            {posts.map((post) => (
              <PostCard
                key={post._id}
                post={post}
                onUpdate={handlePostUpdate}
                onDelete={handlePostDelete}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
