import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import LoadingSpinner from "../components/LoadingSpinner";

const CreatePost = () => {
  const [formData, setFormData] = useState({
    content: "",
    codeSnippet: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
    if (error) setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.content.trim()) {
      setError("Post content is required");
      return;
    }

    try {
      setLoading(true);
      await axios.post("/api/posts", {
        content: formData.content.trim(),
        codeSnippet: formData.codeSnippet.trim(),
      });

      navigate("/feed");
    } catch (error) {
      setError(error.response?.data?.message || "Failed to create post");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto py-8 px-4">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Create a Post</h1>
        <p className="text-gray-600">
          Share your thoughts, ideas, or code with the DevMate community.
        </p>
      </div>

      <form onSubmit={handleSubmit} className="card p-6">
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
            {error}
          </div>
        )}

        <div className="mb-6">
          <label
            htmlFor="content"
            className="block text-sm font-medium text-gray-700 mb-2"
          >
            Content *
          </label>
          <textarea
            id="content"
            name="content"
            rows="6"
            required
            className="w-full p-3 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            placeholder="What's on your mind? Share your ideas, ask questions, or start a discussion..."
            value={formData.content}
            onChange={handleChange}
            maxLength="2000"
          />
          <div className="flex justify-between items-center mt-2">
            <span className="text-xs text-gray-500">
              {formData.content.length}/2000 characters
            </span>
          </div>
        </div>

        <div className="mb-6">
          <label
            htmlFor="codeSnippet"
            className="block text-sm font-medium text-gray-700 mb-2"
          >
            Code Snippet (Optional)
          </label>
          <textarea
            id="codeSnippet"
            name="codeSnippet"
            rows="10"
            className="w-full p-3 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-primary-500 focus:border-transparent font-mono text-sm bg-gray-50"
            placeholder="// Paste your code here
function example() {
  console.log('Hello, DevMate!');
}"
            value={formData.codeSnippet}
            onChange={handleChange}
            maxLength="5000"
          />
          <div className="flex justify-between items-center mt-2">
            <span className="text-xs text-gray-500">
              {formData.codeSnippet.length}/5000 characters
            </span>
            <span className="text-xs text-gray-500">
              Tip: Include language and context for better engagement
            </span>
          </div>
        </div>

        <div className="flex justify-end space-x-4">
          <button
            type="button"
            onClick={() => navigate("/feed")}
            className="btn-secondary"
          >
            Cancel
          </button>
          <button
            type="submit"
            disabled={loading || !formData.content.trim()}
            className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
          >
            {loading ? (
              <>
                <LoadingSpinner size="small" className="mr-2" />
                Publishing...
              </>
            ) : (
              "Publish Post"
            )}
          </button>
        </div>
      </form>

      {/* Preview Section */}
      {formData.content && (
        <div className="mt-8">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Preview</h3>
          <div className="card p-6">
            <div className="flex items-center mb-4">
              <div className="bg-primary-600 text-white w-10 h-10 rounded-full flex items-center justify-center mr-3">
                You
              </div>
              <div>
                <p className="font-semibold text-gray-900">Your Name</p>
                <p className="text-sm text-gray-500">@yourusername • now</p>
              </div>
            </div>

            <div className="mb-4">
              <p className="text-gray-900 whitespace-pre-wrap mb-4">
                {formData.content}
              </p>

              {formData.codeSnippet && (
                <div className="code-snippet">
                  <pre className="whitespace-pre-wrap">
                    {formData.codeSnippet}
                  </pre>
                </div>
              )}
            </div>

            <div className="flex items-center space-x-6 py-2 border-t border-gray-200">
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                  />
                </svg>
                <span>0</span>
              </div>
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"
                  />
                </svg>
                <span>0</span>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreatePost;
