import React, { useState, useRef, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const Navbar = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const menuRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMobileMenuOpen(false);
      }
    }
    function handleScroll() {
      setMobileMenuOpen(false);
    }
    if (mobileMenuOpen) {
      document.addEventListener("mousedown", handleClickOutside);
      window.addEventListener("scroll", handleScroll);
    } else {
      document.removeEventListener("mousedown", handleClickOutside);
      window.removeEventListener("scroll", handleScroll);
    }
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      window.removeEventListener("scroll", handleScroll);
    };
  }, [mobileMenuOpen]);

  return (
    <nav className="fixed top-0 left-0 w-full bg-gradient-to-r from-x-black via-x-dark to-x-black/95 backdrop-blur-xl border-b border-x-border/50 z-[60] shadow-lg shadow-x-black/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo and DevMate text (mobile only) */}
          <Link
            to={user ? "/feed" : "/"}
            className="flex items-center space-x-2 group md:hidden"
          >
            <div className="w-8 h-8 bg-x-blue rounded-full flex items-center justify-center group-hover:bg-x-blue-hover transition-colors">
              <span className="text-white font-bold text-sm">D</span>
            </div>
            <span className="text-2xl font-bold text-x-white group-hover:text-x-blue transition-colors">
              DevMate
            </span>
          </Link>

          {/* Desktop logo only (hidden on mobile) */}
          <Link
            to={user ? "/feed" : "/"}
            className="hidden md:flex items-center group"
          >
            <div className="w-8 h-8 bg-x-blue rounded-full flex items-center justify-center group-hover:bg-x-blue-hover transition-colors">
              <span className="text-white font-bold text-sm">D</span>
            </div>
          </Link>

          {/* Dark Mode Toggle (always visible right) */}
          <button
            className="ml-4 p-2 rounded-full bg-x-dark hover:bg-x-blue transition-colors text-x-gray hover:text-x-white focus:outline-none"
            aria-label="Toggle dark mode"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth={2}
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M21 12.79A9 9 0 1111.21 3a7 7 0 109.79 9.79z"
              />
            </svg>
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
