import React from "react";

const ShimmerEffect = ({ type = "feed" }) => {
  // Shimmer animation class
  const shimmerClass = "animate-shimmer rounded";

  const renderFeedShimmer = () => (
    <div className="max-w-2xl mx-auto py-4 lg:py-8 px-2 lg:px-4 space-y-6">
      {/* Header shimmer */}
      <div className="flex justify-between items-center mb-6 lg:mb-8">
        <div className={`h-8 w-24 rounded-lg ${shimmerClass}`}></div>
        <div className={`h-10 w-32 rounded-full ${shimmerClass}`}></div>
      </div>

      {/* Post cards shimmer */}
      {[1, 2, 3].map((item) => (
        <div key={item} className="x-card space-y-4">
          {/* User info */}
          <div className="flex items-center space-x-3">
            <div className={`w-12 h-12 rounded-full ${shimmerClass}`}></div>
            <div className="space-y-2">
              <div className={`h-4 w-32 rounded ${shimmerClass}`}></div>
              <div className={`h-3 w-24 rounded ${shimmerClass}`}></div>
            </div>
          </div>

          {/* Post content */}
          <div className="space-y-3">
            <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
            <div className={`h-4 w-3/4 rounded ${shimmerClass}`}></div>
            <div className={`h-4 w-1/2 rounded ${shimmerClass}`}></div>
          </div>

          {/* Code block shimmer (sometimes) */}
          {item === 2 && (
            <div className={`h-32 w-full rounded-lg ${shimmerClass}`}></div>
          )}

          {/* Action buttons */}
          <div className="flex items-center space-x-6 pt-4">
            <div className={`h-6 w-16 rounded ${shimmerClass}`}></div>
            <div className={`h-6 w-20 rounded ${shimmerClass}`}></div>
            <div className={`h-6 w-18 rounded ${shimmerClass}`}></div>
          </div>
        </div>
      ))}
    </div>
  );

  const renderProfileShimmer = () => (
    <div className="max-w-6xl mx-auto py-4 lg:py-8 px-3 lg:px-6 space-y-8">
      {/* Hero Profile Section */}
      <div className="relative mb-8">
        {/* Cover Background shimmer */}
        <div className={`h-48 lg:h-64 rounded-t-3xl ${shimmerClass}`}></div>
        
        {/* Profile info section */}
        <div className="relative bg-x-card border border-x-border rounded-b-3xl p-6 lg:p-8 -mt-16 pt-20">
          <div className="flex flex-col lg:flex-row lg:items-start lg:space-x-8 space-y-6 lg:space-y-0">
            {/* Avatar */}
            <div className="flex justify-center lg:justify-start">
              <div className={`w-32 h-32 rounded-full border-4 border-x-black ${shimmerClass}`}></div>
            </div>
            
            {/* User info */}
            <div className="flex-1 text-center lg:text-left space-y-4">
              <div className="space-y-2">
                <div className={`h-8 w-48 rounded mx-auto lg:mx-0 ${shimmerClass}`}></div>
                <div className={`h-4 w-32 rounded mx-auto lg:mx-0 ${shimmerClass}`}></div>
              </div>
              
              <div className="space-y-2">
                <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
                <div className={`h-4 w-3/4 rounded mx-auto lg:mx-0 ${shimmerClass}`}></div>
              </div>
              
              {/* Skills shimmer */}
              <div className="flex flex-wrap gap-2 justify-center lg:justify-start">
                {[1, 2, 3, 4].map((skill) => (
                  <div key={skill} className={`h-6 w-16 rounded-full ${shimmerClass}`}></div>
                ))}
              </div>
            </div>
          </div>
          
          {/* Stats */}
          <div className="grid grid-cols-3 gap-6 mt-8 pt-6 border-t border-x-border/30">
            {[1, 2, 3].map((stat) => (
              <div key={stat} className="text-center space-y-2">
                <div className={`h-6 w-12 rounded mx-auto ${shimmerClass}`}></div>
                <div className={`h-4 w-16 rounded mx-auto ${shimmerClass}`}></div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Tabs shimmer */}
      <div className="border-b border-x-border/30 mb-8">
        <div className="flex space-x-8">
          <div className={`h-10 w-16 rounded-t ${shimmerClass}`}></div>
          <div className={`h-10 w-20 rounded-t ${shimmerClass}`}></div>
        </div>
      </div>

      {/* Posts grid shimmer */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {[1, 2, 3, 4].map((item) => (
          <div key={item} className="x-card space-y-4">
            <div className="space-y-3">
              <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
              <div className={`h-4 w-3/4 rounded ${shimmerClass}`}></div>
            </div>
            <div className="flex items-center space-x-6">
              <div className={`h-6 w-16 rounded ${shimmerClass}`}></div>
              <div className={`h-6 w-20 rounded ${shimmerClass}`}></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderPostDetailShimmer = () => (
    <div className="max-w-2xl mx-auto py-8 px-4 space-y-6">
      {/* Back button */}
      <div className="mb-6">
        <div className={`h-10 w-24 rounded ${shimmerClass}`}></div>
      </div>

      {/* Post detail */}
      <div className="x-card space-y-6">
        {/* User info */}
        <div className="flex items-center space-x-3">
          <div className={`w-12 h-12 rounded-full ${shimmerClass}`}></div>
          <div className="space-y-2">
            <div className={`h-4 w-32 rounded ${shimmerClass}`}></div>
            <div className={`h-3 w-24 rounded ${shimmerClass}`}></div>
          </div>
        </div>

        {/* Post content */}
        <div className="space-y-4">
          <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
          <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
          <div className={`h-4 w-3/4 rounded ${shimmerClass}`}></div>
          <div className={`h-40 w-full rounded-lg ${shimmerClass}`}></div>
        </div>

        {/* Actions */}
        <div className="flex items-center space-x-6">
          <div className={`h-6 w-16 rounded ${shimmerClass}`}></div>
          <div className={`h-6 w-20 rounded ${shimmerClass}`}></div>
        </div>
      </div>

      {/* Comments section */}
      <div className="space-y-4">
        <div className={`h-6 w-24 rounded ${shimmerClass}`}></div>
        {[1, 2].map((comment) => (
          <div key={comment} className="flex space-x-3">
            <div
              className={`w-8 h-8 rounded-full flex-shrink-0 ${shimmerClass}`}
            ></div>
            <div className="flex-1 space-y-2">
              <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
              <div className={`h-4 w-2/3 rounded ${shimmerClass}`}></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderExploreShimmer = () => (
    <div className="w-full max-w-7xl mx-auto py-4 lg:py-8 px-2 lg:px-6 space-y-8">
      {/* Hero Section */}
      <div className="mb-6 lg:mb-8">
        <div className={`h-8 w-64 rounded ${shimmerClass} mb-3`}></div>
        <div className={`h-4 w-96 rounded ${shimmerClass}`}></div>
      </div>

      {/* Search and filters */}
      <div className="bg-x-card border border-x-border rounded-xl p-6 mb-8 space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className={`h-12 w-full rounded-lg ${shimmerClass}`}></div>
          <div className={`h-12 w-full rounded-lg ${shimmerClass}`}></div>
          <div className={`h-10 w-24 rounded-lg ${shimmerClass}`}></div>
        </div>
      </div>

      {/* User cards grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {[1, 2, 3, 4, 5, 6].map((item) => (
          <div key={item} className="x-card space-y-4">
            <div className="flex items-center space-x-4">
              <div className={`w-16 h-16 rounded-full ${shimmerClass}`}></div>
              <div className="space-y-2">
                <div className={`h-5 w-32 rounded ${shimmerClass}`}></div>
                <div className={`h-4 w-24 rounded ${shimmerClass}`}></div>
              </div>
            </div>
            <div className="space-y-2">
              <div className={`h-4 w-full rounded ${shimmerClass}`}></div>
              <div className={`h-4 w-3/4 rounded ${shimmerClass}`}></div>
            </div>
            <div className="flex flex-wrap gap-2">
              {[1, 2, 3].map((skill) => (
                <div
                  key={skill}
                  className={`h-6 w-16 rounded-full ${shimmerClass}`}
                ></div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  switch (type) {
    case "profile":
      return renderProfileShimmer();
    case "post-detail":
      return renderPostDetailShimmer();
    case "explore":
      return renderExploreShimmer();
    case "feed":
    default:
      return renderFeedShimmer();
  }
};

export default ShimmerEffect;
