import React from "react";
import { Link, useLocation } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const Sidebar = () => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const isFooterVisible = useFooterVisibility();

  const navItems = [
    { path: "/feed", icon: "üè†", label: "Home" },
    { path: "/explore", icon: "üîç", label: "Explore" },
    { path: "/create-post", icon: "‚úèÔ∏è", label: "Post" },
    { path: `/profile/${user?.username}`, icon: "üë§", label: "Profile" },
  ];

  return (
    <div className={`x-sidebar-mobile transition-opacity duration-300 ${isFooterVisible ? 'opacity-0 pointer-events-none' : 'opacity-100'}`}>
      {/* Logo - Only visible on larger screens */}
      <div className="mb-8 hidden lg:block">
        <Link to="/feed" className="text-2xl font-bold text-x-blue">
          DevMate
        </Link>
      </div>

      {/* Navigation */}
      <nav className="space-y-2">
        {navItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center space-x-4 p-3 rounded-full hover:bg-x-darker transition-colors text-x-white ${
              location.pathname === item.path ? "bg-x-darker" : ""
            }`}
          >
            <span className="text-xl">{item.icon}</span>
            <span className="text-xl font-medium hidden lg:block">
              {item.label}
            </span>
          </Link>
        ))}
      </nav>

      {/* Post Button */}
      <Link
        to="/create-post"
        className="x-button w-full mt-8 text-center hidden lg:block"
      >
        Post
      </Link>

      {/* Floating Post Button for Mobile */}
      <Link
        to="/create-post"
        className={`lg:hidden fixed bottom-6 right-6 w-14 h-14 bg-x-blue hover:bg-x-blue-hover rounded-full flex items-center justify-center text-white text-2xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-110 z-50 ${isFooterVisible ? 'opacity-0 pointer-events-none translate-y-4' : 'opacity-100 translate-y-0'}`}
      >
        ‚úèÔ∏è
      </Link>

      {/* User Info - Only visible on larger screens */}
      <div className="mt-auto pt-8 hidden lg:block">
        <div className="flex items-center space-x-3 p-3 rounded-full hover:bg-x-darker transition-colors">
          <div className="w-10 h-10 bg-x-blue rounded-full flex items-center justify-center">
            <span className="text-white font-bold">
              {user?.displayName?.[0] || user?.username?.[0] || "U"}
            </span>
          </div>
          <div className="flex-1">
            <p className="font-bold text-x-white">
              {user?.displayName || user?.username}
            </p>
            <p className="text-x-gray text-sm">@{user?.username}</p>
          </div>
          <button
            onClick={logout}
            className="text-x-gray hover:text-x-white"
            title="Logout"
          >
            üö™
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
