import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import ShimmerEffect from "../components/ShimmerEffect";

const FollowersList = () => {
  const { username } = useParams();
  const [followers, setFollowers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchFollowers = async () => {
      try {
        setLoading(true);
        const res = await axios.get(`/api/users/${username}/followers`);
        setFollowers(res.data.followers || []);
      } catch (err) {
        setError("Failed to fetch followers");
      } finally {
        setLoading(false);
      }
    };
    fetchFollowers();
  }, [username]);

  if (loading) return <ShimmerEffect type="profile" />;
  if (error) return <div className="text-red-500 p-8">{error}</div>;

  return (
    <div className="max-w-2xl mx-auto py-8 px-4">
      <h2 className="text-2xl font-bold text-x-white mb-6">Followers of @{username}</h2>
      {followers.length === 0 ? (
        <div className="text-x-gray">No followers yet.</div>
      ) : (
        <ul className="space-y-4">
          {followers.map((follower) => (
            <li key={follower._id} className="bg-x-dark/60 p-4 rounded-xl flex items-center gap-4">
              <div className="bg-gradient-to-r from-x-blue to-purple-500 text-white w-12 h-12 rounded-full flex items-center justify-center text-xl font-bold">
                {follower.displayName?.charAt(0).toUpperCase() || follower.username.charAt(0).toUpperCase()}
              </div>
              <div>
                <Link to={`/profile/${follower.username}`} className="text-lg font-semibold text-x-white hover:text-x-blue">
                  {follower.displayName || follower.username}
                </Link>
                <div className="text-x-gray text-sm">@{follower.username}</div>
              </div>
            </li>
          ))}
        </ul>
      )}
      <div className="mt-8">
        <Link to={`/profile/${username}`} className="text-x-blue hover:underline">Back to Profile</Link>
      </div>
    </div>
  );
};

export default FollowersList;
