import React from "react";
import { Link, useLocation } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const FloatingPostButton = () => {
  const { user } = useAuth();
  const location = useLocation();

  // Only show on specific pages when user is logged in
  const showOnPages = ["/feed", "/explore", "/profile", "/post"];
  const shouldShow =
    user &&
    showOnPages.some(
      (page) => location.pathname.startsWith(page) || location.pathname === "/"
    );

  // Don't show on create-post page itself
  if (!shouldShow || location.pathname === "/create-post") {
    return null;
  }

  return (
    <Link
      to="/create-post"
      className="lg:hidden sticky bottom-4 right-4 w-14 h-14 floating-post-btn rounded-full flex items-center justify-center transition-all duration-300 z-50 group shadow-lg"
      title="Create Post"
      style={{
        position: "sticky",
        bottom: "1rem",
        right: "1rem",
        float: "right",
        marginLeft: "auto",
        marginBottom: "1rem"
      }}
    >
      <span
        className="text-2xl group-hover:rotate-12 group-hover:scale-110 transition-all duration-300 drop-shadow-lg"
        role="img"
        aria-label="pencil"
      >
        ✏️
      </span>
    </Link>
  );
};

export default FloatingPostButton;
